import { useState } from "react";

// ============================================
// SIGNAL TRACKER DATA
// ============================================
const DOMINOS = [
  { id: 1, name: "SaaS Compression", color: "#E63946", icon: "◉", description: "Enterprise software pricing power collapse as in-house AI becomes credible BATNA",
    signals: [
      { name: "Public SaaS Net Revenue Retention", source: "Quarterly earnings (SNOW, NOW, CRM, ZS)", frequency: "Quarterly", currentStatus: "amber", baseline: "120-130% NRR was healthy", threshold: "Below 110% = compression confirmed", notes: "Watch Q1 2026 earnings cycle closely", dataPoints: [{ date: "Q3 2025", value: "118% avg", status: "amber" }] },
      { name: "Indeed SaaS Sales Job Postings", source: "Indeed Hiring Lab", frequency: "Monthly", currentStatus: "amber", baseline: "Indexed to Jan 2020 = 100", threshold: "Below 60 = structural decline", notes: "Track 'SaaS', 'Account Executive', 'SDR' categories", dataPoints: [] },
      { name: "Y Combinator 'Replace X' Startups", source: "YC batch announcements", frequency: "Per batch (2x/year)", currentStatus: "amber", baseline: "~5-10% of batch historically", threshold: ">25% of batch = acceleration", notes: "Count startups explicitly positioning as SaaS replacements", dataPoints: [] },
      { name: "Enterprise Software Spending Forecasts", source: "Gartner, IDC", frequency: "Quarterly", currentStatus: "green", baseline: "8-12% annual growth", threshold: "Below 5% or negative = contraction", notes: "Distinguish between AI spend (up) and legacy SaaS (down)", dataPoints: [] },
    ],
  },
  { id: 2, name: "White-Collar Displacement", color: "#F4A261", icon: "◈", description: "Professional/business services employment decline accelerates beyond normal cyclical",
    signals: [
      { name: "JOLTS: Professional Services Openings", source: "BLS JOLTS Report", frequency: "Monthly", currentStatus: "amber", baseline: "~2M openings in professional/business services", threshold: "Below 1.5M = structural shift", notes: "Compare professional services vs. blue collar divergence", dataPoints: [] },
      { name: "BLS Employment: Information Sector", source: "BLS Employment Situation", frequency: "Monthly (first Friday)", currentStatus: "amber", baseline: "~3M employed in information sector", threshold: "3 consecutive months of decline = trend confirmed", notes: "Also track 'financial activities' and 'professional services'", dataPoints: [] },
      { name: "Challenger Layoff Announcements (Tech/Finance)", source: "Challenger, Gray & Christmas", frequency: "Monthly", currentStatus: "amber", baseline: "~30-50K/month tech+finance layoffs in 2024", threshold: ">100K/month = acceleration", notes: "Watch for 'AI' or 'efficiency' cited as reason", dataPoints: [] },
      { name: "Initial Jobless Claims Composition", source: "DOL + ADP/Equifax breakdowns", frequency: "Weekly (Thursday)", currentStatus: "green", baseline: "~220K weekly, mixed collar", threshold: ">350K with white-collar concentration = crisis", notes: "The article's crisis trigger was 487K", dataPoints: [] },
    ],
  },
  { id: 3, name: "Friction Collapse", color: "#2A9D8F", icon: "◆", description: "AI agents disintermediate consumer transactions and destroy habitual moats",
    signals: [
      { name: "DoorDash/Uber Eats Take Rate", source: "Quarterly earnings", frequency: "Quarterly", currentStatus: "green", baseline: "~15-20% take rate", threshold: "Below 12% = price competition from agents", notes: "Watch for mentions of 'multi-platform' or 'agent' in earnings calls", dataPoints: [] },
      { name: "Visa/Mastercard Purchase Volume Growth", source: "V/MA quarterly earnings", frequency: "Quarterly", currentStatus: "green", baseline: "5-8% YoY growth", threshold: "Below 3% = agent routing around interchange", notes: "Article cited MA Q1 2027 at +3.4% as inflection", dataPoints: [] },
      { name: "Stablecoin Transaction Volume", source: "On-chain data (Dune, DefiLlama)", frequency: "Daily/Weekly", currentStatus: "green", baseline: "~$500B/month (2025)", threshold: ">$2T/month = mainstream agent adoption", notes: "Solana + Ethereum L2 volumes specifically", dataPoints: [] },
      { name: "Real Estate Commission Compression", source: "NAR data, Redfin reports", frequency: "Quarterly", currentStatus: "amber", baseline: "2.5-3% buy-side commission", threshold: "Below 1.5% in major metros = agent disruption", notes: "NAR settlement already started this trend", dataPoints: [] },
    ],
  },
  { id: 4, name: "Ghost GDP", color: "#264653", icon: "◇", description: "Output grows but money stops circulating through the human economy",
    signals: [
      { name: "GDP Growth vs. Real Wage Growth Spread", source: "BEA GDP + BLS wage data", frequency: "Quarterly", currentStatus: "amber", baseline: "Typically track within 1-2pp", threshold: ">4pp divergence = Ghost GDP confirmed", notes: "The key diagnostic: productivity up, wages down", dataPoints: [] },
      { name: "M2 Velocity of Money", source: "Federal Reserve (FRED)", frequency: "Quarterly", currentStatus: "amber", baseline: "~1.2 (post-COVID)", threshold: "Below 1.0 = money not circulating", notes: "Already at historic lows; further decline = systemic", dataPoints: [] },
      { name: "Consumer Confidence vs. CEO Confidence", source: "Conference Board + C-Suite Survey", frequency: "Monthly", currentStatus: "green", baseline: "Usually correlated", threshold: ">20pt divergence = two-economy dynamic", notes: "CEOs bullish on AI, consumers bearish on jobs", dataPoints: [] },
      { name: "Labor Share of GDP", source: "BLS Productivity Report", frequency: "Quarterly", currentStatus: "amber", baseline: "56% (2024)", threshold: "Below 52% = acceleration of capital capture", notes: "Article projected drop to 46% by 2028", dataPoints: [] },
    ],
  },
  { id: 5, name: "Financial Contagion", color: "#9B2226", icon: "◐", description: "Private credit defaults cascade through insurance-PE structure into mortgages",
    signals: [
      { name: "PE-Backed Software Default Rate", source: "Preqin, PitchBook, Moody's", frequency: "Quarterly", currentStatus: "green", baseline: "<2% default rate", threshold: ">5% = systemic stress", notes: "Watch for sector-wide downgrades like 2015 energy", dataPoints: [] },
      { name: "Alt Manager Stock Prices (BX, APO, KKR)", source: "Market data", frequency: "Daily", currentStatus: "green", baseline: "Trading at/near ATH", threshold: ">25% drawdown = market pricing contagion", notes: "Insurance subsidiary exposure is the transmission mechanism", dataPoints: [] },
      { name: "Prime Mortgage Delinquency (Tech Metros)", source: "Fannie Mae, Freddie Mac, Zillow", frequency: "Monthly", currentStatus: "green", baseline: "<1% 90+ day delinquency", threshold: ">2% in SF/SEA/AUS = early warning", notes: "Watch HELOC draws and 401k withdrawals as leading indicators", dataPoints: [] },
      { name: "NAIC Insurance Capital Actions", source: "NAIC, state regulator filings", frequency: "As announced", currentStatus: "green", baseline: "Normal oversight", threshold: "Capital treatment changes for private credit = crisis mode", notes: "The Athene/Apollo scenario from the article", dataPoints: [] },
    ],
  },
  { id: 6, name: "Policy Response", color: "#6D6875", icon: "◑", description: "Government capacity and speed to address structural displacement",
    signals: [
      { name: "Congressional AI Legislation Activity", source: "Congress.gov, news tracking", frequency: "Ongoing", currentStatus: "amber", baseline: "Hearings and proposals", threshold: "Bipartisan bill with funding = response accelerating", notes: "Watch for 'compute tax', 'transition economy', 'AI dividend'", dataPoints: [] },
      { name: "Fed Language: Structural vs. Cyclical", source: "FOMC minutes, speeches", frequency: "8x/year (FOMC) + speeches", currentStatus: "green", baseline: "'Cyclical softening'", threshold: "'Structural displacement' in official language = paradigm shift", notes: "The article has Warsh using 'daisy chain' in Nov 2027", dataPoints: [] },
      { name: "Federal Receipts vs. CBO Baseline", source: "Treasury Monthly Statement", frequency: "Monthly", currentStatus: "green", baseline: "Within 2-3% of projection", threshold: ">8% below baseline = fiscal stress", notes: "Article cites 12% below baseline by Q1 2028", dataPoints: [] },
      { name: "Deficit Trajectory", source: "CBO, Treasury", frequency: "Quarterly", currentStatus: "amber", baseline: "~6% of GDP (2025)", threshold: ">9% without recession stimulus = structural", notes: "Compare to COVID 15% — but that was understood as temporary", dataPoints: [] },
    ],
  },
];

const STATUS_CFG = {
  green: { label: "Baseline", bg: "rgba(42,157,143,0.12)", border: "rgba(42,157,143,0.3)", dot: "#2A9D8F", text: "#2A9D8F" },
  amber: { label: "Watch", bg: "rgba(244,162,97,0.12)", border: "rgba(244,162,97,0.3)", dot: "#F4A261", text: "#E9C46A" },
  red: { label: "Alert", bg: "rgba(230,57,70,0.12)", border: "rgba(230,57,70,0.3)", dot: "#E63946", text: "#E63946" },
};

// ============================================
// LUCID BOX PRODUCT DATA
// ============================================
const PRODUCTS = [
  { tier: "Free", name: "Content Engine", price: "$0", color: "#6D6875", format: "Twitter/LinkedIn/Blog", audience: "Everyone — top of funnel", description: "Every thread, every workflow screenshot, every 'here's what my AI system did today' post. The product demo that runs 24/7.", margin: "N/A — marketing", volume: "3-5x/week", status: "launch_now", alos: "ALOS Content Pipeline agent drafts. You edit + post. 30 min/day." },
  { tier: "Digital", name: "ALOS Workflow Library", price: "$29-97", color: "#2A9D8F", format: "Gumroad/Lemon Squeezy download", audience: "DIY learners, Claude power users", description: "50+ Claude Projects packaged as templates with system prompts and usage guides. Starter ($29, 10 workflows), Pro ($67, 25), Complete ($97, 50+ with ALOS methodology guide).", margin: "~95%", volume: "50-100 sales/mo → $2.5-10K/mo", status: "build_this_month", alos: "These ARE the ALOS outputs, repackaged. Inventory already built." },
  { tier: "Workshop", name: "Claude Code Launchpad", price: "$297-497", color: "#E9C46A", format: "Virtual, 3hr live, 10-20 ppl", audience: "Remote learners, small biz", description: "Pick ONE pipeline and build it live in 3 hours. Includes pre-session setup guide and 7-day Slack access.", margin: "~90%. $297×15=$4.5K/session", volume: "2x/mo = $9-15K/mo", status: "launch_month_2", alos: "Curriculum from Power Day learnings." },
  { tier: "Power Day", name: "Claude Code Power Day", price: "$1.5K→$3K", color: "#E63946", format: "In-person, Austin, 8hr, 1-4 ppl", audience: "Solopreneurs, creators, execs", description: "The flagship. Walk in with zero Claude Code experience, walk out with 4 live pipelines built and deployed.", margin: "~90%", volume: "3-4x/mo = $9-12K/mo", status: "launch_now", alos: "The Power Day IS building a personal ALOS." },
  { tier: "Power Day+", name: "Power Day + 30-Day Coaching", price: "$5,000", color: "#F4A261", format: "In-person + 4 weekly calls + Slack", audience: "Serious builders who want accountability", description: "Power Day plus one month of guided implementation. Weekly calls to troubleshoot and expand pipelines.", margin: "~88%", volume: "2-3/mo = $10-15K/mo", status: "launch_month_2", alos: "Coaching helps clients build ALOS to your level." },
  { tier: "Enterprise", name: "Team AI Enablement", price: "$8-15K", color: "#9B2226", format: "2-3 day, on-site/remote, 5-15 ppl", audience: "Startups, SMBs, teams", description: "Full team transformation: workflow audit → custom pipeline build → training → 30-day support.", margin: "~85%", volume: "1-2/mo = $8-30K/mo", status: "launch_quarter_2", alos: "Enterprise clients get team-level ALOS architecture." },
  { tier: "Retainer", name: "AI Advisory", price: "$3-5K/mo", color: "#264653", format: "2 calls/mo + async Slack + quarterly builds", audience: "Post-engagement enterprise clients", description: "Fractional AI enablement lead. Keep their AI adoption evolving as capabilities improve.", margin: "~90% recurring", volume: "3-5 clients = $9-25K/mo", status: "launch_quarter_3", alos: "Helping them maintain their organizational ALOS." },
  { tier: "Course", name: "Build Your AI Operating System", price: "$497-997", color: "#6D6875", format: "Self-paced video + community + weekly Q&A", audience: "Global remote audience", description: "6 modules covering all 4 pipelines + ALOS architecture. Community access + 12 weeks live Q&A.", margin: "~95% evergreen", volume: "$10-30K/mo at maturity", status: "build_quarter_3", alos: "The course teaches people to build what you've built." },
];

const ALOS_COMPONENTS = [
  { name: "Knowledge Management Pipeline", readiness: "HIGH", desc: "Obsidian + Claude integration. Capture → organize → retrieve → synthesize.", demo: "Show your actual vault: '6 months of knowledge, instantly searchable and synthesizable.'" },
  { name: "Content Pipeline", readiness: "HIGH", desc: "Research → outline → draft → edit → publish workflow.", demo: "'Watch me create a LinkedIn post in 5 minutes using this pipeline.'" },
  { name: "Non-Dev Development Pipeline", readiness: "HIGH", desc: "Idea → prototype → deployed app without coding experience.", demo: "'I built a production app with 24 agents. I'm not a developer.'" },
  { name: "Claude Projects Library", readiness: "HIGHEST", desc: "50+ pre-built Claude Project configs with system prompts.", demo: "Screenshot gallery: 'A different AI coworker for every task in my life.'" },
  { name: "Mobile Capture Workflow", readiness: "MEDIUM", desc: "Phone → Obsidian → ALOS auto-processing.", demo: "Video: capture idea on phone, appears in Obsidian, Claude processes it." },
  { name: "ALOS Architecture Methodology", readiness: "MEDIUM", desc: "The meta-framework: how to design your own multi-agent AI system.", demo: "Architecture diagram showing all 9 agents and decision flows. Signature IP." },
  { name: "Trust Stack / Verification", readiness: "LOW-MED", desc: "Framework for verifying AI outputs against ground truth.", demo: "Enterprise: 'How do you trust AI outputs in regulated environments?'" },
];

const TWITTER_PILLARS = [
  { pillar: "Build in Public", freq: "2-3x/week", target: "AI builders, indie hackers, Claude community", examples: ["Thread: 'I built a 24-agent platform. I'm not a developer.'", "Claude Code terminal screenshots with commentary", "'Day 47: Today I shipped [X]. Architecture decision and why.'"] },
  { pillar: "ALOS Showcase", freq: "1-2x/week", target: "Productivity enthusiasts, knowledge workers", examples: ["'My AI OS handled 12 tasks at my son's wrestling tournament.'", "Screenshot tour of Claude Projects library", "'How I manage full-time job + two kids + 3 projects.'"] },
  { pillar: "Intelligence Crisis Analysis", freq: "1x/week", target: "Fintwit, macro analysts, investors", examples: ["'Signal Tracker says Domino 2 went amber. Here's what JOLTS means.'", "Quote-tweet macro accounts with your framework", "'Everyone watches NVDA. Here's the number that actually matters.'"] },
  { pillar: "Enterprise AI Adoption", freq: "1x/week", target: "AI lab GTM teams, enterprise leaders", examples: ["'I manage 50-70 enterprise accounts. Here's what I see about AI adoption.'", "'Why the gap between buying Claude Enterprise and 3x productivity is $100B.'", "'The procurement conversation has changed. Here's what buyers care about.'"] },
];

const REVENUE_SCENARIOS = [
  { scenario: "Conservative", color: "#2A9D8F", months: [
    { m: "Mar", pd: 1500, ws: 0, dg: 200, ent: 0, ret: 0 },
    { m: "Apr", pd: 3000, ws: 0, dg: 500, ent: 0, ret: 0 },
    { m: "May", pd: 6000, ws: 2970, dg: 800, ent: 0, ret: 0 },
    { m: "Jun", pd: 6000, ws: 2970, dg: 1200, ent: 8000, ret: 0 },
    { m: "Jul", pd: 9000, ws: 4455, dg: 1500, ent: 0, ret: 3000 },
    { m: "Aug", pd: 9000, ws: 4455, dg: 2000, ent: 12000, ret: 3000 },
  ]},
  { scenario: "Moderate", color: "#E9C46A", months: [
    { m: "Mar", pd: 3000, ws: 0, dg: 400, ent: 0, ret: 0 },
    { m: "Apr", pd: 6000, ws: 2970, dg: 1000, ent: 0, ret: 0 },
    { m: "May", pd: 9000, ws: 4455, dg: 2000, ent: 8000, ret: 0 },
    { m: "Jun", pd: 12000, ws: 4455, dg: 3000, ent: 12000, ret: 3000 },
    { m: "Jul", pd: 12000, ws: 7425, dg: 4000, ent: 12000, ret: 6000 },
    { m: "Aug", pd: 15000, ws: 7425, dg: 5000, ent: 15000, ret: 9000 },
  ]},
];

// ============================================
// SHARED STYLES
// ============================================
const S = {
  label: { fontSize: 10, color: "rgba(255,255,255,0.35)", textTransform: "uppercase", letterSpacing: "1.2px", marginBottom: 6, fontFamily: "'IBM Plex Mono', monospace" },
  card: (c) => ({ background: "rgba(255,255,255,0.025)", border: `1px solid ${c || "rgba(255,255,255,0.06)"}`, borderRadius: 10, padding: "14px 16px", marginBottom: 10 }),
  tab: (a, c) => ({ padding: "7px 12px", fontSize: 11, fontWeight: a ? 700 : 500, color: a ? c : "rgba(255,255,255,0.4)", background: a ? `${c}15` : "transparent", border: `1px solid ${a ? `${c}33` : "transparent"}`, borderRadius: 7, cursor: "pointer", transition: "all 0.2s" }),
  sectionTab: (a, c) => ({ padding: "10px 20px", fontSize: 13, fontWeight: a ? 700 : 500, color: a ? "#E8E4DF" : "rgba(255,255,255,0.35)", background: a ? `${c}18` : "transparent", border: `1px solid ${a ? `${c}44` : "rgba(255,255,255,0.06)"}`, borderRadius: 8, cursor: "pointer", transition: "all 0.2s", letterSpacing: "-0.2px" }),
};

const Badge = ({ text, color }) => (
  <span style={{ fontSize: 9, fontWeight: 700, color, background: `${color}15`, padding: "2px 6px", borderRadius: 3, letterSpacing: "0.5px" }}>{text}</span>
);

// ============================================
// SIGNAL TRACKER COMPONENTS
// ============================================
function SignalCard({ signal, dominoColor }) {
  const [exp, setExp] = useState(false);
  const st = STATUS_CFG[signal.currentStatus];
  return (
    <div onClick={() => setExp(!exp)} style={{ background: "rgba(255,255,255,0.03)", border: `1px solid ${exp ? dominoColor + "44" : "rgba(255,255,255,0.06)"}`, borderRadius: 8, padding: "12px 14px", cursor: "pointer", transition: "all 0.2s", marginBottom: 6 }}>
      <div style={{ display: "flex", alignItems: "center", justifyContent: "space-between" }}>
        <div style={{ display: "flex", alignItems: "center", gap: 8, flex: 1 }}>
          <div style={{ width: 7, height: 7, borderRadius: "50%", background: st.dot, flexShrink: 0, boxShadow: `0 0 5px ${st.dot}66` }} />
          <span style={{ fontSize: 13, fontWeight: 500 }}>{signal.name}</span>
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
          <span style={{ fontSize: 10, color: st.text, background: st.bg, border: `1px solid ${st.border}`, padding: "2px 7px", borderRadius: 4, fontWeight: 600, letterSpacing: "0.5px", textTransform: "uppercase" }}>{st.label}</span>
          <span style={{ color: "rgba(255,255,255,0.25)", fontSize: 11, transform: exp ? "rotate(180deg)" : "rotate(0)", transition: "transform 0.2s" }}>▾</span>
        </div>
      </div>
      {exp && (
        <div style={{ marginTop: 12, paddingTop: 12, borderTop: "1px solid rgba(255,255,255,0.06)" }}>
          <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 8, marginBottom: 10 }}>
            <div><div style={S.label}>Source</div><div style={{ fontSize: 12, color: "rgba(255,255,255,0.65)" }}>{signal.source}</div></div>
            <div><div style={S.label}>Frequency</div><div style={{ fontSize: 12, color: "rgba(255,255,255,0.65)" }}>{signal.frequency}</div></div>
          </div>
          <div style={{ marginBottom: 8 }}><div style={S.label}>Baseline</div><div style={{ fontSize: 12, color: "rgba(255,255,255,0.65)" }}>{signal.baseline}</div></div>
          <div style={{ marginBottom: 8 }}><div style={{ ...S.label, color: dominoColor, fontWeight: 600 }}>Trigger Threshold</div><div style={{ fontSize: 12, color: "#E8E4DF", fontWeight: 500 }}>{signal.threshold}</div></div>
          <div style={{ fontSize: 11, color: "rgba(255,255,255,0.45)", fontStyle: "italic", background: "rgba(255,255,255,0.02)", padding: "7px 10px", borderRadius: 5, borderLeft: `2px solid ${dominoColor}33` }}>{signal.notes}</div>
          {signal.dataPoints.length > 0 && (
            <div style={{ marginTop: 8 }}>{signal.dataPoints.map((dp, i) => (
              <div key={i} style={{ display: "flex", alignItems: "center", gap: 6, fontSize: 11, color: "rgba(255,255,255,0.55)" }}>
                <div style={{ width: 5, height: 5, borderRadius: "50%", background: STATUS_CFG[dp.status].dot }} />
                <span style={{ color: "rgba(255,255,255,0.35)" }}>{dp.date}</span><span>{dp.value}</span>
              </div>
            ))}</div>
          )}
        </div>
      )}
    </div>
  );
}

function DominoSection({ domino, isActive, onToggle }) {
  const sc = { green: 0, amber: 0, red: 0 };
  domino.signals.forEach(s => sc[s.currentStatus]++);
  const tl = sc.red >= 2 ? "CRITICAL" : sc.red >= 1 || sc.amber >= 3 ? "ELEVATED" : sc.amber >= 2 ? "WATCH" : "BASELINE";
  const tc = tl === "CRITICAL" ? "#E63946" : tl === "ELEVATED" ? "#F4A261" : tl === "WATCH" ? "#E9C46A" : "#2A9D8F";
  return (
    <div style={{ marginBottom: 12 }}>
      <div onClick={onToggle} style={{ display: "flex", alignItems: "center", justifyContent: "space-between", padding: "14px 16px", background: isActive ? `linear-gradient(135deg, ${domino.color}11, ${domino.color}08)` : "rgba(255,255,255,0.02)", border: `1px solid ${isActive ? domino.color + "33" : "rgba(255,255,255,0.06)"}`, borderRadius: isActive ? "10px 10px 0 0" : 10, cursor: "pointer", transition: "all 0.2s" }}>
        <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
          <span style={{ fontSize: 18, color: domino.color }}>{domino.icon}</span>
          <div>
            <span style={{ fontSize: 14, fontWeight: 600 }}>Domino {domino.id}: {domino.name}</span>
            <div style={{ fontSize: 11, color: "rgba(255,255,255,0.35)", marginTop: 2 }}>{domino.description}</div>
          </div>
        </div>
        <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
          <div style={{ display: "flex", gap: 3 }}>
            {Object.entries(sc).map(([s, c]) => c > 0 ? <div key={s} style={{ fontSize: 10, color: STATUS_CFG[s].text, background: STATUS_CFG[s].bg, padding: "2px 6px", borderRadius: 3 }}>{c}</div> : null)}
          </div>
          <span style={{ fontSize: 9, fontWeight: 700, color: tc, letterSpacing: "1px" }}>{tl}</span>
        </div>
      </div>
      {isActive && (
        <div style={{ padding: "14px 16px", background: "rgba(0,0,0,0.15)", border: `1px solid ${domino.color}22`, borderTop: "none", borderRadius: "0 0 10px 10px" }}>
          {domino.signals.map((sig, i) => <SignalCard key={i} signal={sig} dominoColor={domino.color} />)}
        </div>
      )}
    </div>
  );
}

// ============================================
// MAIN COMPONENT
// ============================================
export default function CommandCenter() {
  const [section, setSection] = useState("lucidbox");
  const [subTab, setSubTab] = useState("products");
  const [activeDominos, setActiveDominos] = useState(new Set([1]));
  const [expandedProduct, setExpandedProduct] = useState(3);

  const toggleDomino = (id) => { setActiveDominos(prev => { const n = new Set(prev); n.has(id) ? n.delete(id) : n.add(id); return n; }); };

  // Signal calculations
  const totalSig = DOMINOS.reduce((s, d) => s + d.signals.length, 0);
  const amberCt = DOMINOS.reduce((s, d) => s + d.signals.filter(x => x.currentStatus === "amber").length, 0);
  const redCt = DOMINOS.reduce((s, d) => s + d.signals.filter(x => x.currentStatus === "red").length, 0);
  const greenCt = totalSig - amberCt - redCt;
  const threat = redCt >= 6 ? "CRISIS" : redCt >= 3 ? "CRITICAL" : amberCt >= 12 ? "ELEVATED" : amberCt >= 6 ? "WATCH" : "BASELINE";
  const threatClr = threat === "CRISIS" || threat === "CRITICAL" ? "#E63946" : threat === "ELEVATED" ? "#F4A261" : threat === "WATCH" ? "#E9C46A" : "#2A9D8F";

  const statusLabels = { launch_now: "LAUNCH NOW", build_this_month: "THIS MONTH", launch_month_2: "MONTH 2", launch_quarter_2: "Q2", launch_quarter_3: "Q3", build_quarter_3: "BUILD Q3" };
  const statusColors = { launch_now: "#E63946", build_this_month: "#F4A261", launch_month_2: "#E9C46A", launch_quarter_2: "#2A9D8F", launch_quarter_3: "#6D6875", build_quarter_3: "#264653" };

  // Set appropriate sub-tab when switching sections
  const switchSection = (s) => {
    setSection(s);
    if (s === "lucidbox") setSubTab("products");
    else setSubTab("dominos");
  };

  return (
    <div style={{ minHeight: "100vh", background: "#0D0D0F", color: "#E8E4DF", fontFamily: "'IBM Plex Sans', -apple-system, sans-serif", padding: "20px 14px" }}>
      <style>{`@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Mono:wght@400;500;600&display=swap'); * { box-sizing: border-box; margin: 0; padding: 0; } ::-webkit-scrollbar { width: 4px; } ::-webkit-scrollbar-thumb { background: rgba(255,255,255,0.1); border-radius: 4px; }`}</style>

