export function useFredData() {
  return useQuery({
    queryKey: ['fred-data'],
    queryFn: async () => {
      const data = await fetchAllFredData();

      // Cache in Supabase
      await supabase.from('live_data_cache').upsert({
        data_key: 'fred_all',
        data: data,
        fetched_at: new Date().toISOString(),
        ttl_minutes: 1440, // 24 hours
      }, { onConflict: 'data_key' });

      return data;
    },
    staleTime: 24 * 60 * 60 * 1000,     // 24 hours
    gcTime: 7 * 24 * 60 * 60 * 1000,    // 7 days
    refetchOnWindowFocus: false,
    refetchInterval: false,               // daily data, no polling — manual refetch or page load
  });
}
