const COINGECKO_BASE = 'https://api.coingecko.com/api/v3';

export async function fetchStablecoinData() {
  // Fetch stablecoin market caps
  const res = await fetch(
    `${COINGECKO_BASE}/coins/markets?vs_currency=usd&ids=tether,usd-coin,dai&order=market_cap_desc`
  );
  if (!res.ok) throw new Error(`CoinGecko API error: ${res.status}`);
  const coins = await res.json();

  const totalMarketCap = coins.reduce((sum, c) => sum + (c.market_cap || 0), 0);
  const totalVolume = coins.reduce((sum, c) => sum + (c.total_volume || 0), 0);

  return {
    coins,
    totalMarketCap,
    totalVolume,
    fetchedAt: new Date().toISOString(),
  };
}
