  const totalSig = liveDominos.reduce((s, d) => s + d.signals.length, 0);
  const amberCt = liveDominos.reduce((s, d) => s + d.signals.filter(x => x.currentStatus === "amber").length, 0);
  const redCt = liveDominos.reduce((s, d) => s + d.signals.filter(x => x.currentStatus === "red").length, 0);
  const greenCt = totalSig - amberCt - redCt;
  const threat = redCt >= 6 ? "CRISIS" : redCt >= 3 ? "CRITICAL" : amberCt >= 12 ? "ELEVATED" : amberCt >= 6 ? "WATCH" : "BASELINE";
  const threatClr = threat === "CRISIS" || threat === "CRITICAL" ? "#E63946" : threat === "ELEVATED" ? "#F4A261" : threat === "WATCH" ? "#E9C46A" : "#2A9D8F";

  // Feed status
