    { name: "FRED", active: !!FRED_API_KEY, loading: fredLoading, data: !!fredData },
    { name: "Stocks", active: !!TWELVE_DATA_API_KEY, loading: stockLoading, data: !!stockData },
    { name: "Crypto", active: true, loading: cryptoLoading, data: !!cryptoData },
  ];

