import { useState, useMemo } from "react";
import DominoSection from "./DominoSection";
import { Badge, S } from "../styles";
import { ALOS_COMPONENTS } from "../data/alos-components";
import { DOMINOS } from "../data/dominos";
import { PRODUCTS } from "../data/products";
import { REVENUE_SCENARIOS } from "../data/revenue";
import { TWITTER_PILLARS } from "../data/twitter";
import { useFredData } from "../hooks/useFredData";
import { useStockData } from "../hooks/useStockData";
import { useCryptoData } from "../hooks/useCryptoData";
import { useSignalStatuses } from "../hooks/useSignalStatuses";
import { useAutoThreshold } from "../hooks/useAutoThreshold";
import { FRED_API_KEY } from "../lib/fred";
import { TWELVE_DATA_API_KEY } from "../lib/stocks";

export default function CommandCenter() {
  const [section, setSection] = useState("lucidbox");
  const [subTab, setSubTab] = useState("products");
  const [activeDominos, setActiveDominos] = useState(new Set([1]));
  const [expandedProduct, setExpandedProduct] = useState(3);

  // Live data feeds
  const { data: fredData, isLoading: fredLoading } = useFredData();
  const { data: stockData, isLoading: stockLoading } = useStockData();
  const { data: cryptoData, isLoading: cryptoLoading } = useCryptoData();
  const { data: signalStatuses } = useSignalStatuses();

  // Auto-threshold evaluation
  const thresholdResult = useAutoThreshold({
    fredData,
    stockData,
    cryptoData,
    signalStatuses,
  });

  // Merge Supabase statuses onto static DOMINOS (Supabase wins, static is fallback)
  const liveDominos = useMemo(() => {
    if (!signalStatuses?.length) return DOMINOS;
    return DOMINOS.map((d) => ({
      ...d,
      signals: d.signals.map((sig) => {
        const live = signalStatuses.find(
          (s) => s.domino_id === d.id && s.signal_name === sig.name,
        );
        if (!live) return sig;
        return {
          ...sig,
          currentStatus: live.status,
          isOverride: live.is_override,
          updatedAt: live.updated_at,
          updatedBy: live.updated_by,
        };
      }),
    }));
  }, [signalStatuses]);

  const toggleDomino = (id) => {
    setActiveDominos((prev) => {
      const n = new Set(prev);
      n.has(id) ? n.delete(id) : n.add(id);
      return n;
    });
  };

  // Signal calculations (from live data when available)
  const totalSig = liveDominos.reduce((s, d) => s + d.signals.length, 0);
  const amberCt = liveDominos.reduce(
    (s, d) => s + d.signals.filter((x) => x.currentStatus === "amber").length,
    0,
  );
  const redCt = liveDominos.reduce(
    (s, d) => s + d.signals.filter((x) => x.currentStatus === "red").length,
    0,
  );
  const greenCt = totalSig - amberCt - redCt;
  const threat =
    redCt >= 6
      ? "CRISIS"
      : redCt >= 3
        ? "CRITICAL"
        : amberCt >= 12
          ? "ELEVATED"
          : amberCt >= 6
            ? "WATCH"
            : "BASELINE";
  const threatClr =
    threat === "CRISIS" || threat === "CRITICAL"
      ? "#E63946"
      : threat === "ELEVATED"
        ? "#F4A261"
        : threat === "WATCH"
          ? "#E9C46A"
          : "#2A9D8F";

  // Feed status
  const feeds = [
    {
      name: "FRED",
      active: !!FRED_API_KEY,
      loading: fredLoading,
      data: !!fredData,
    },
    {
      name: "Stocks",
      active: !!TWELVE_DATA_API_KEY,
      loading: stockLoading,
      data: !!stockData,
    },
    {
      name: "Crypto",
      active: true,
      loading: cryptoLoading,
      data: !!cryptoData,
    },
  ];

  const statusLabels = {
    launch_now: "LAUNCH NOW",
    build_this_month: "THIS MONTH",
    launch_month_2: "MONTH 2",
    launch_quarter_2: "Q2",
    launch_quarter_3: "Q3",
    build_quarter_3: "BUILD Q3",
  };
  const statusColors = {
    launch_now: "#E63946",
    build_this_month: "#F4A261",
    launch_month_2: "#E9C46A",
    launch_quarter_2: "#2A9D8F",
    launch_quarter_3: "#6D6875",
    build_quarter_3: "#264653",
  };

  // Set appropriate sub-tab when switching sections
  const switchSection = (s) => {
    setSection(s);
    if (s === "lucidbox") setSubTab("products");
    else setSubTab("dominos");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#0D0D0F",
        color: "#E8E4DF",
        fontFamily: "'IBM Plex Sans', -apple-system, sans-serif",
        padding: "20px 14px",
      }}
    >
      <div style={{ maxWidth: 920, margin: "0 auto" }}>
        {/* Master Header */}
        <div style={{ marginBottom: 20 }}>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: 8,
              marginBottom: 4,
            }}
          >
            <div
              style={{
                width: 3,
                height: 24,
                background: "linear-gradient(180deg, #E9C46A, #E63946)",
                borderRadius: 2,
              }}
            />
            <h1
              style={{ fontSize: 20, fontWeight: 700, letterSpacing: "-0.8px" }}
            >
              Asymmetric Bridge
            </h1>
            <span
              style={{
                fontSize: 9,
                color: "#E9C46A",
                background: "#E9C46A15",
                padding: "2px 8px",
                borderRadius: 4,
                fontWeight: 600,
                marginLeft: 6,
              }}
            >
              COMMAND CENTER
            </span>
          </div>
          <p
            style={{
              fontSize: 10,
              color: "rgba(255,255,255,0.2)",
              marginLeft: 11,
              fontFamily: "'IBM Plex Mono'",
            }}
          >
            Lucid Box Products · Signal Tracker · ALOS Productization · Revenue
            Model · Twitter Strategy
          </p>
        </div>

        {/* Section Switcher */}
        <div style={{ display: "flex", gap: 8, marginBottom: 16 }}>
          <button
            onClick={() => switchSection("lucidbox")}
            style={S.sectionTab(section === "lucidbox", "#E63946")}
          >
            Lucid Box Business
          </button>
          <button
            onClick={() => switchSection("signals")}
            style={S.sectionTab(section === "signals", "#E9C46A")}
          >
            Signal Tracker
            <span
              style={{
                marginLeft: 6,
                fontSize: 9,
                color: threatClr,
                fontWeight: 700,
              }}
            >
              {threat}
            </span>
          </button>
        </div>

        {/* ================= LUCID BOX SECTION ================= */}
        {section === "lucidbox" && (
          <div>
            <div
              style={{
                display: "flex",
                gap: 4,
                marginBottom: 14,
                flexWrap: "wrap",
              }}
            >
              {[
                { id: "products", label: "Products", c: "#E63946" },
                { id: "alos", label: "ALOS → Products", c: "#2A9D8F" },
                { id: "twitter", label: "Twitter/X", c: "#1DA1F2" },
                { id: "revenue", label: "Revenue", c: "#F4A261" },
                { id: "content", label: "Content", c: "#E9C46A" },
              ].map((t) => (
                <button
                  key={t.id}
                  onClick={() => setSubTab(t.id)}
                  style={S.tab(subTab === t.id, t.c)}
                >
                  {t.label}
                </button>
              ))}
            </div>

            {/* PRODUCTS */}
            {subTab === "products" && (
              <div>
                <div
                  style={{
                    ...S.card("rgba(255,255,255,0.06)"),
                    padding: "14px",
                    marginBottom: 14,
                  }}
                >
                  <div style={S.label}>Product Ladder (Click to Expand)</div>
                  <div
                    style={{
                      display: "flex",
                      alignItems: "flex-end",
                      justifyContent: "space-between",
                      height: 100,
                      padding: "0 4px",
                      marginTop: 8,
                    }}
                  >
                    {PRODUCTS.map((p, i) => {
                      const h = [12, 28, 42, 58, 68, 80, 88, 100];
                      return (
                        <div
                          key={i}
                          style={{
                            display: "flex",
                            flexDirection: "column",
                            alignItems: "center",
                            gap: 3,
                            cursor: "pointer",
                          }}
                          onClick={() =>
                            setExpandedProduct(expandedProduct === i ? -1 : i)
                          }
                        >
                          <div
                            style={{
                              fontSize: 8,
                              color: p.color,
                              fontWeight: 600,
                              fontFamily: "'IBM Plex Mono'",
                              textAlign: "center",
                              maxWidth: 50,
                            }}
                          >
                            {p.price.split(" ")[0]}
                          </div>
                          <div
                            style={{
                              width: 30,
                              height: `${h[i]}%`,
                              minHeight: 10,
                              background: `linear-gradient(180deg, ${p.color}88, ${p.color}33)`,
                              borderRadius: "3px 3px 0 0",
                              border: `1px solid ${expandedProduct === i ? p.color : `${p.color}44`}`,
                              transition: "all 0.2s",
                            }}
                          />
                          <div
                            style={{
                              fontSize: 7,
                              color: "rgba(255,255,255,0.25)",
                              textAlign: "center",
                              maxWidth: 50,
                              lineHeight: 1.1,
                            }}
                          >
                            {p.tier}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </div>

                {PRODUCTS.map((p, i) => (
                  <div
                    key={i}
                    style={{
                      ...S.card(
                        expandedProduct === i
                          ? `${p.color}33`
                          : "rgba(255,255,255,0.04)",
                      ),
                      cursor: "pointer",
                      opacity:
                        expandedProduct !== -1 && expandedProduct !== i
                          ? 0.45
                          : 1,
                      transition: "all 0.2s",
                    }}
                    onClick={() =>
                      setExpandedProduct(expandedProduct === i ? -1 : i)
                    }
                  >
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                      }}
                    >
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: 8,
                        }}
                      >
                        <div
                          style={{
                            width: 6,
                            height: 6,
                            borderRadius: 2,
                            background: p.color,
                          }}
                        />
                        <span style={{ fontSize: 13, fontWeight: 600 }}>
                          {p.name}
                        </span>
                        <span
                          style={{
                            fontSize: 10,
                            color: "rgba(255,255,255,0.25)",
                          }}
                        >
                          {p.tier}
                        </span>
                      </div>
                      <div
                        style={{
                          display: "flex",
                          alignItems: "center",
                          gap: 6,
                        }}
                      >
                        <Badge
                          text={statusLabels[p.status]}
                          color={statusColors[p.status]}
                        />
                        <span
                          style={{
                            fontSize: 14,
                            fontWeight: 700,
                            color: p.color,
                          }}
                        >
                          {p.price}
                        </span>
                      </div>
                    </div>
                    {expandedProduct === i && (
                      <div
                        style={{
                          marginTop: 10,
                          paddingTop: 10,
                          borderTop: "1px solid rgba(255,255,255,0.06)",
                        }}
                      >
                        <div
                          style={{
                            fontSize: 12,
                            color: "rgba(255,255,255,0.6)",
                            lineHeight: 1.6,
                            marginBottom: 8,
                          }}
                        >
                          {p.description}
                        </div>
                        <div
                          style={{
                            display: "grid",
                            gridTemplateColumns: "1fr 1fr 1fr",
                            gap: 6,
                            marginBottom: 8,
                          }}
                        >
                          <div>
                            <div style={S.label}>Format</div>
                            <div
                              style={{
                                fontSize: 10,
                                color: "rgba(255,255,255,0.45)",
                              }}
                            >
                              {p.format}
                            </div>
                          </div>
                          <div>
                            <div style={S.label}>Margin</div>
                            <div
                              style={{
                                fontSize: 10,
                                color: "rgba(255,255,255,0.45)",
                              }}
                            >
                              {p.margin}
                            </div>
                          </div>
                          <div>
                            <div style={S.label}>Volume</div>
                            <div
                              style={{
                                fontSize: 10,
                                color: "rgba(255,255,255,0.45)",
                              }}
                            >
                              {p.volume}
                            </div>
                          </div>
                        </div>
                        <div
                          style={{
                            background: `${p.color}08`,
                            borderLeft: `2px solid ${p.color}33`,
                            padding: "6px 10px",
                            borderRadius: "0 5px 5px 0",
                          }}
                        >
                          <div
                            style={{
                              fontSize: 9,
                              color: p.color,
                              fontWeight: 600,
                              letterSpacing: "0.8px",
                            }}
                          >
                            ALOS CONNECTION
                          </div>
                          <div
                            style={{
                              fontSize: 10,
                              color: "rgba(255,255,255,0.45)",
                            }}
                          >
                            {p.alos}
                          </div>
                        </div>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* ALOS PRODUCTIZATION */}
            {subTab === "alos" && (
              <div>
                <div style={{ ...S.card("#2A9D8F33"), marginBottom: 14 }}>
                  <div style={S.label}>ALOS Current State</div>
                  <div
                    style={{
                      display: "grid",
                      gridTemplateColumns: "repeat(3, 1fr)",
                      gap: 6,
                      marginTop: 6,
                    }}
                  >
                    {[
                      ["9", "Agents"],
                      ["29+", "Skills"],
                      ["50+", "Claude Projects"],
                      ["50+", "Premium Workflows"],
                      ["Kosha Prime", "Obsidian Vault"],
                      ["3", "Integrations"],
