      if (prices.length === 0) return null;
      // Check drawdown from 52-week high
      const avgDrawdown = prices.reduce((sum, p) => {
        const pctFrom52 = p.fifty_two_week?.high
          ? ((parseFloat(p.close) - parseFloat(p.fifty_two_week.high)) / parseFloat(p.fifty_two_week.high)) * 100
          : parseFloat(p.percent_change) || 0;
        return sum + pctFrom52;
      }, 0) / prices.length;
      return { value: avgDrawdown, label: `Avg drawdown from 52w high: ${avgDrawdown.toFixed(1)}%` };
    },
    thresholds: [
      { operator: "<", value: -25, status: "red", reason: ">25% drawdown — market pricing contagion" },
      { operator: "<", value: -15, status: "amber", reason: ">15% drawdown — stress emerging" },
    ],
  },
  {
    domino_id: 5,
    signal_name: "Prime Mortgage Delinquency (Tech Metros)",
    manual_only: true,
  },
  {
    domino_id: 5,
    signal_name: "NAIC Insurance Capital Actions",
    manual_only: true,
  },

  // ============================================
  // DOMINO 6: Policy Response
  // ============================================
  {
    domino_id: 6,
    signal_name: "Congressional AI Legislation Activity",
    manual_only: true,
  },
  {
    domino_id: 6,
    signal_name: "Fed Language: Structural vs. Cyclical",
    manual_only: true,
  },
  {
    domino_id: 6,
    signal_name: "Federal Receipts vs. CBO Baseline",
    data_source: "fred",
    manual_only: false,
    fred_series: "FGRECPT",
    extract: (fredData) => {
      const series = fredData?.["FGRECPT"];
      if (!series?.observations?.[0]) return null;
      const val = parseFloat(series.observations[0].value);
      return { value: val, label: `Federal receipts: $${(val / 1000).toFixed(0)}B` };
    },
    // Receipts alone don't tell us % below baseline without CBO data — mostly manual
    thresholds: [],
    notes: "Auto-evaluation limited — needs CBO baseline comparison (manual)",
  },
  {
    domino_id: 6,
    signal_name: "Deficit Trajectory",
    manual_only: true,
  },
];

// Count of auto-evaluable vs manual-only signals
export const RULE_STATS = {
  total: THRESHOLD_RULES.length,
  auto: THRESHOLD_RULES.filter((r) => !r.manual_only && r.thresholds?.length > 0).length,
  manual: THRESHOLD_RULES.filter((r) => r.manual_only || !r.thresholds?.length).length,
};

